    const metrics = [
      { label: 'Physical Readiness', value: 87, color: [255, 99, 132] },
      { label: 'Mental Readiness', value: 92, color: [54, 162, 235] },
      { label: 'Technical Skills', value: 85, color: [255, 205, 86] },
      { label: 'Overall Performance', value: 88, color: [75, 192, 192] }
    ];

