@tailwind components;
@tailwind utilities;

:root {
  --background: 0 0% 100%; /* #FFFFFF */
  --foreground: 240 10% 3.9%; /* #0A0A0B */
  --muted: 240 4.8% 95.9%; /* #F4F4F5 */
  --muted-foreground: 240 3.8% 46.1%; /* #71717A */
  --popover: 0 0% 100%; /* #FFFFFF */
  --popover-foreground: 240 10% 3.9%; /* #0A0A0B */
  --card: 0 0% 100%; /* #FFFFFF */
  --card-foreground: 240 10% 3.9%; /* #0A0A0B */
  --border: 240 5.9% 90%; /* #E4E4E7 */
  --input: 240 5.9% 90%; /* #E4E4E7 */
  --primary: 220 91% 50%; /* #1E40AF - Taekwondo Blue */
  --primary-foreground: 210 40% 98%; /* #F1F5F9 */
  --secondary: 158 64% 42%; /* #059669 */
  --secondary-foreground: 0 0% 98%; /* #FAFAFA */
  --accent: 43 96% 56%; /* #F59E0B */
  --accent-foreground: 0 0% 98%; /* #FAFAFA */
  --success: 158 64% 52%; /* #10B981 */
  --warning: 43 96% 56%; /* #F59E0B */
  --danger: 0 84% 60%; /* #EF4444 */
  --destructive: 0 84% 60%; /* #EF4444 */
  --destructive-foreground: 0 0% 98%; /* #FAFAFA */
  --ring: 240 10% 3.9%; /* #0A0A0B */
  --radius: 0.5rem;

  /* Sport-specific color variables */
