  --sport-gradient-to: 220 91% 35%;

  /* Chart colors */
  --chart-1: 220 91% 50%; /* #1E40AF */
  --chart-2: 158 64% 42%; /* #059669 */
  --chart-3: 43 96% 56%; /* #F59E0B */
  --chart-4: 0 84% 60%; /* #EF4444 */
  --chart-5: 270 95% 75%; /* #A855F7 */

  /* Sidebar colors */
  --sidebar-background: 0 0% 100%; /* #FFFFFF */
  --sidebar-foreground: 240 5.9% 10%; /* #18181B */
  --sidebar-primary: 220 91% 50%; /* #1E40AF */
  --sidebar-primary-foreground: 210 40% 98%; /* #F1F5F9 */
  --sidebar-accent: 240 4.8% 95.9%; /* #F4F4F5 */
  --sidebar-accent-foreground: 240 5.9% 10%; /* #18181B */
  --sidebar-border: 240 5.9% 90%; /* #E4E4E7 */
  --sidebar-ring: 240 10% 3.9%; /* #0A0A0B */
}

.dark {
  --background: 240 10% 3.9%; /* #0A0A0B */
  --foreground: 0 0% 98%; /* #FAFAFA */
  --muted: 240 3.7% 15.9%; /* #27272A */
