}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    overflow-x: hidden;
    width: 100%;
    max-width: 100vw;
  }

  html {
    overflow-x: hidden;
    width: 100%;
  }

  #root {
    overflow-x: hidden;
    width: 100%;
    max-width: 100vw;
  }

  @media (max-width: 768px) {
    * {
      max-width: 100vw;
    }
  }
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 6px;
}

::-webkit-scrollbar-track {
  background: hsl(var(--muted));
}

::-webkit-scrollbar-thumb {
  background: hsl(var(--border));
  border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--muted-foreground));
}

@layer components {
  /* Modern glass effect */
  .glass-effect {
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  /* Gradient animations */
  .gradient-animate {
    background: linear-gradient(-45deg, #ee7752, #e73c7e, #23a6d5, #23d5ab);
    background-size: 400% 400%;
    animation: gradientShift 15s ease infinite;
  }

  /* Sport-specific utility classes */
  .sport-primary {
    background-color: hsl(var(--sport-primary));
    color: hsl(var(--sport-primary-foreground));
  }

  .sport-primary-text {
    color: hsl(var(--sport-primary));
  }

  .sport-accent {
    background-color: hsl(var(--sport-accent));
    color: hsl(var(--sport-primary-foreground));
  }

  .sport-gradient {
    background: linear-gradient(135deg, 
      hsl(var(--sport-gradient-from)), 
      hsl(var(--sport-gradient-to))
    );
  }

  .sport-border {
    border-color: hsl(var(--sport-primary));
  }

  .sport-ring {
    --tw-ring-color: hsl(var(--sport-primary));
  }

  @keyframes gradientShift {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }

  /* Smooth hover effects */
  .hover-lift {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .hover-lift:hover {
    transform: translateY(-4px);
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  }

  /* Pulse animation for live indicators */
  .pulse-gentle {
    animation: pulseGentle 2s ease-in-out infinite;
  }

  @keyframes pulseGentle {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.7; }
  }

  /* Shimmer effect for loading states */
  .shimmer {
    background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
    background-size: 200% 100%;
    animation: shimmer 1.5s infinite;
  }

  @keyframes shimmer {
    0% { background-position: -200% 0; }
    100% { background-position: 200% 0; }
  }

  /* Floating animation */
  .float {
    animation: float 6s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  /* Scale on hover */
  .scale-hover {
    transition: transform 0.2s ease-in-out;
  }

  .scale-hover:hover {
    transform: scale(1.02);
  }

  /* Smooth border radius animation */
  .border-radius-animate {
    transition: border-radius 0.3s ease-in-out;
  }

  .border-radius-animate:hover {
    border-radius: 1.5rem;
  }

  /* Modern card glow effect */
  .card-glow {
    position: relative;
    overflow: hidden;
  }

  .card-glow::before {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    background: linear-gradient(45deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    border-radius: inherit;
    z-index: -1;
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  .card-glow:hover::before {
    opacity: 1;
  }

  /* Mobile Responsive Enhancements */
  .touch-target {
