                <Sparkles className="absolute -top-1 -right-1 h-4 w-4 opacity-30" />
              </div>
              <p className="font-medium">No upcoming competitions scheduled</p>
              <p className="text-sm mt-1 opacity-75">Competition schedule will be updated as events are announced</p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Recent Competition Results */}
      <Card className="border-0 shadow-lg bg-gradient-to-br from-white to-amber-50/30 dark:from-gray-900 dark:to-amber-950/10">
        <CardHeader className="pb-4">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-amber-100 dark:bg-amber-900/50 rounded-lg">
              <Trophy className="h-5 w-5 text-amber-600 dark:text-amber-400" />
            </div>
            <div>
              <CardTitle className="text-lg font-bold">Recent Results</CardTitle>
              <CardDescription className="text-sm">Latest competition outcomes</CardDescription>
            </div>
          </div>
        </CardHeader>
        <CardContent className="pt-0">
          {recentCompetitions.length > 0 ? (
            <div className="space-y-3">
              {recentCompetitions.map((competition) => (
                <div key={competition.id} className="group flex items-center justify-between p-4 rounded-xl border bg-gradient-to-r from-white to-amber-50/30 dark:from-gray-800 dark:to-amber-900/10 hover:shadow-md transition-all duration-200">
                  <div className="flex items-center space-x-4">
                    <div className={`p-2 rounded-full ${
                      competition.competitionLevel === 'olympic' ? 'bg-yellow-100 dark:bg-yellow-900/30' :
                      competition.competitionLevel === 'world_championship' ? 'bg-purple-100 dark:bg-purple-900/30' :
                      'bg-amber-100 dark:bg-amber-900/30'
                    }`}>
                      <Trophy className={`h-4 w-4 ${
                        competition.competitionLevel === 'olympic' ? 'text-yellow-600 dark:text-yellow-400' :
                        competition.competitionLevel === 'world_championship' ? 'text-purple-600 dark:text-purple-400' :
                        'text-amber-600 dark:text-amber-400'
                      }`} />
                    </div>
                    <div>
                      <p className="font-semibold text-foreground">{competition.title}</p>
                      <div className="flex items-center space-x-4 text-sm text-muted-foreground">
                        <span className="font-medium">{format(new Date(competition.date), 'MMM dd, yyyy')}</span>
                        {competition.location && (
                          <span className="flex items-center space-x-1">
                            <MapPin className="h-3 w-3" />
                            <span>{competition.location}</span>
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Badge className={`${getCompetitionLevelColor(competition.competitionLevel)} font-medium`}>
                      {formatCompetitionLevel(competition.competitionLevel)}
                    </Badge>
                    <div className="opacity-0 group-hover:opacity-100 transition-opacity">
                      <Star className="h-4 w-4 text-amber-500" />
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8 text-muted-foreground">
              <div className="relative inline-flex items-center justify-center w-12 h-12 rounded-full bg-muted/50 mb-3">
                <Trophy className="h-6 w-6 opacity-50" />
                <Sparkles className="absolute -top-1 -right-1 h-3 w-3 opacity-30" />
              </div>
              <p className="font-medium">No recent competition results</p>
              <p className="text-sm mt-1 opacity-75">Results will appear as competitions conclude</p>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/dashboard/kpi-metr.content:
import { Card, CardContent } from "@/components/ui/card";
import { Target, RotateCcw, Activity, Brain, TrendingUp, TrendingDown } from "lucide-react";
import type { KpiMetric } from "@shared/schema";

interface KpiMetricsProps {
  kpis: KpiMetric[];
}

const getIcon = (metricName: string) => {
  switch (metricName.toLowerCase()) {
    case "serve accuracy":
      return Target;
    case "return game":
      return RotateCcw;
    case "court coverage":
      return Activity;
    case "mental resilience":
      return Brain;
    default:
      return Target;
  }
};

const getIconColor = (metricName: string) => {
  switch (metricName.toLowerCase()) {
    case "serve accuracy":
      return "text-primary";
    case "return game":
      return "text-secondary";
    case "court coverage":
      return "text-accent";
    case "mental resilience":
      return "text-purple-600";
    default:
      return "text-primary";
  }
};

const getBgColor = (metricName: string) => {
  switch (metricName.toLowerCase()) {
    case "serve accuracy":
      return "bg-primary/10";
    case "return game":
      return "bg-secondary/10";
    case "court coverage":
      return "bg-accent/10";
    case "mental resilience":
      return "bg-purple-100";
    default:
      return "bg-primary/10";
  }
};

export default function KpiMetrics({ kpis }: KpiMetricsProps) {
  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      {kpis.map((kpi) => {
        const Icon = getIcon(kpi.metricName);
        const trend = parseFloat(kpi.trend || "0");
        const isPositive = trend > 0;

        return (
          <Card key={kpi.id}>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600">{kpi.metricName}</p>
                  <p className="text-2xl font-bold text-gray-900 mt-2">
                    {parseFloat(kpi.value).toFixed(1)}%
                  </p>
                </div>
                <div className={`w-12 h-12 ${getBgColor(kpi.metricName)} rounded-lg flex items-center justify-center`}>
                  <Icon className={`${getIconColor(kpi.metricName)}`} />
                </div>
              </div>
              <div className="mt-4 flex items-center">
                <span className={`text-xs ${isPositive ? 'text-success' : 'text-warning'}`}>
                  {isPositive ? (
                    <TrendingUp className="inline w-3 h-3 mr-1" />
                  ) : (
                    <TrendingDown className="inline w-3 h-3 mr-1" />
                  )}
                  {isPositive ? '+' : ''}{trend.toFixed(1)}%
                </span>
                <span className="text-xs text-gray-500 ml-2">vs last month</span>
              </div>
            </CardContent>
          </Card>
        );
      })}
    </div>
  );
}

