                    <p className="font-semibold text-foreground truncate">
                      {athlete.name}
                    </p>
                    {athlete.olympicRank && athlete.olympicRank <= 3 && (
                      <Star
                        className={`h-4 w-4 ${
                          athlete.olympicRank === 1
                            ? "text-yellow-500"
                            : athlete.olympicRank === 2
                              ? "text-gray-500"
                              : "text-orange-500"
                        }`}
                      />
                    )}
                  </div>
                  <p className="text-sm text-muted-foreground">
                    <span className="mr-1">{getCountryFlagWithFallback(athlete.nationality)}</span>
                    {athlete.nationality} ‚Ä¢ {athlete.olympicCategory}
                  </p>
                </div>

                <div className="flex items-center space-x-2">
                  {athlete.olympicRank && athlete.olympicRank <= 3 && (
                    <Medal
                      className={`h-5 w-5 ${
                        athlete.olympicRank === 1
                          ? "text-yellow-500"
                          : athlete.olympicRank === 2
                            ? "text-gray-500"
                            : "text-orange-500"
                      }`}
                    />
                  )}
                  {athlete.olympicRankChange !== undefined && (
                    <div className="flex items-center">
                      {(() => {
                        const change = athlete.olympicRankChange!;
                        const iconClass = "h-3 w-3";

                        if (change === 0) {
                          return (
                            <div className="flex items-center gap-1 text-xs px-1.5 py-0.5 rounded-md bg-gray-100 dark:bg-gray-800/50 text-gray-600 dark:text-gray-400">
                              <Minus className={iconClass} />
                            </div>
                          );
                        }

                        const Icon = change > 0 ? TrendingUp : TrendingDown;
                        const colorClass = change > 0 
                          ? "bg-green-100 dark:bg-green-900/30 text-green-700 dark:text-green-400"
                          : "bg-red-100 dark:bg-red-900/30 text-red-700 dark:text-red-400";

                        return (
                          <div className={`flex items-center gap-1 text-xs px-1.5 py-0.5 rounded-md ${colorClass}`}>
                            <Icon className={iconClass} />
                            <span>{Math.abs(change)}</span>
                          </div>
                        );
                      })()} 
                    </div>
                  )}
                </div>
              </div>
            ))}
            {olympicRankings.length === 0 && (
              <div className="text-center py-12 text-muted-foreground">
                <div className="relative inline-flex items-center justify-center w-16 h-16 rounded-full bg-muted/50 mb-4">
                  <Award className="h-8 w-8 opacity-50" />
                  <Sparkles className="absolute -top-1 -right-1 h-4 w-4 opacity-30" />
                </div>
                <p className="font-medium">No Olympic rankings available</p>
                <p className="text-sm mt-1 opacity-75">
                  Olympic ranked athletes will appear here
                </p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

