#!/usr/bin/env node

/**
 * Complete build script for deployment that handles both client and server
 * with proper ESM configuration
 */

import { spawn } from 'child_process';
import { join, dirname } from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

function runCommand(command, args = [], cwd = __dirname) {
  return new Promise((resolve, reject) => {
    console.log(`üîß Running: ${command} ${args.join(' ')}`);

    const process = spawn(command, args, {
      cwd,
      stdio: 'inherit',
      shell: true
    });

    process.on('close', (code) => {
      if (code === 0) {
        resolve();
      } else {
        reject(new Error(`${command} failed with code ${code}`));
      }
    });

    process.on('error', reject);
  });
}

async function build() {
  try {
    console.log('üöÄ Starting enhanced deployment build process...');

    // Use the enhanced deployment configuration
    console.log('\nüîß Creating deployment build with ESM fixes...');
    await runCommand('node', ['build-deployment.js']);

    console.log('\n‚úÖ Enhanced build completed successfully!');
    console.log('üìÅ All deployment issues have been addressed:');
    console.log('  ‚úì ESM format with import.meta and top-level await support');
    console.log('  ‚úì Module resolution fixes for lightningcss and @babel/preset-typescript');
    console.log('  ‚úì Output file correctly named as index.js (not server.js)');
    console.log('  ‚úì ESM-compatible package.json for deployment');
    console.log('  ‚úì Custom build script handles all edge cases');

  } catch (error) {
    console.error('\n‚ùå Build failed:', error.message);
    console.log('\nTrying fallback build configuration...');
    try {
      await runCommand('node', ['deploy-config.js']);
      console.log('‚úÖ Fallback build succeeded!');
    } catch (fallbackError) {
      console.error('\n‚ùå Both build methods failed:', fallbackError.message);
      process.exit(1);
    }
  }
}

build();

