#!/usr/bin/env node

/**
 * Custom ESM-compatible build script for deployment
 * Fixes CommonJS/ESM compatibility issues and module format conflicts
 */

import { build } from 'esbuild';
import { fileURLToPath } from 'url';
import { dirname, join } from 'path';
import { readFileSync, writeFileSync, mkdirSync, existsSync } from 'fs';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
const rootDir = join(__dirname, '..');

async function buildServer() {
  try {
    console.log('üöÄ Building server with ESM format for deployment...');

    // Ensure dist directory exists
    const distDir = join(rootDir, 'dist');
    if (!existsSync(distDir)) {
      mkdirSync(distDir, { recursive: true });
    }

    await build({
      entryPoints: [join(rootDir, 'server/index.ts')],
      bundle: true,
      platform: 'node',
      format: 'esm', // ESM format to support import.meta and top-level await
      outfile: join(rootDir, 'dist/index.js'), // Output to index.js as expected by start script
      target: 'node18',
      sourcemap: true,
      minify: process.env.NODE_ENV === 'production',
      treeShaking: true,
      metafile: true,
      logLevel: 'info',
      external: [
        // Core Node.js modules
        'fs',
        'path',
        'url',
        'crypto',
        'http',
        'https',
        'stream',
        'util',
        'events',
        'buffer',
        'os',
        'child_process',
        'querystring',
        'net',
        'tls',

        // Large external dependencies that should not be bundled
        '@neondatabase/serverless',
        'ws',
        'express',
        'passport',
        'passport-local',
        'passport-google-oauth20',
        'passport-microsoft',
        'bcryptjs',
        'multer',
        'openai',
        'puppeteer',
        'drizzle-orm',
        'drizzle-kit',
        'zod',
        'zod-validation-error',

        // Session and storage
        'express-session',
        'connect-pg-simple',
        '@replit/database',
        '@replit/object-storage',
        'memorystore',

        // PDF and utilities
        'jspdf',
        'html2canvas',
        'memoizee',
        'date-fns',

        // React and UI (should not be in server bundle)
        'react',
        'react-dom',
        '@radix-ui/*',
        'lucide-react',

        // Build tools and style libraries that cause issues
        'vite',
        '@vitejs/plugin-react',
        'autoprefixer',
        'tailwindcss',
        'postcss',
        'lightningcss',
        '@babel/preset-typescript',
        '@replit/vite-plugin-cartographer',
        '@replit/vite-plugin-runtime-error-modal',
        '@tailwindcss/vite',
        '@tailwindcss/typography',

        // Additional problematic packages for deployment
        'typescript',
        'tsx',
        'esbuild',
        '@types/*',

        // UI and frontend-only packages
        'framer-motion',
        'react-hook-form',
        'react-day-picker',
        'recharts',
        'embla-carousel-react',
        'react-resizable-panels',
        'react-icons',
        '@tanstack/react-query',
        'vaul',
        'wouter',
        'next-themes',
        'input-otp',
        'cmdk',
        'class-variance-authority',
        'clsx',
        'tailwind-merge',
        'tailwindcss-animate',
        'tw-animate-css'
      ],
      define: {
        'process.env.NODE_ENV': JSON.stringify(process.env.NODE_ENV || 'production'),
        'import.meta.env.MODE': JSON.stringify('production'),
        'import.meta.env.PROD': 'true',
        'import.meta.env.DEV': 'false',
      },
      banner: {
        js: `
// ESM compatibility setup for Node.js deployment
import { createRequire } from 'module';
import { fileURLToPath } from 'url';
import { dirname } from 'path';

const require = createRequire(import.meta.url);
const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

// Polyfill globals for packages expecting CommonJS
if (typeof global !== 'undefined') {
  global.__filename = __filename;
  global.__dirname = __dirname;
  global.require = require;
}
`
      }
    });

    // Create a production package.json for deployment
    const originalPackageJson = JSON.parse(
      readFileSync(join(rootDir, 'package.json'), 'utf-8')
    );

    const productionPackageJson = {
      name: originalPackageJson.name,
      version: originalPackageJson.version,
      type: "module", // Ensure ESM module type
      main: "index.js",
      engines: {
        node: ">=18.0.0"
      },
      scripts: {
        start: "node index.js"
      },
      dependencies: originalPackageJson.dependencies
    };

    writeFileSync(
      join(distDir, 'package.json'),
      JSON.stringify(productionPackageJson, null, 2)
    );

    console.log('‚úÖ Server built successfully with ESM format!');
    console.log('üì¶ Output: dist/index.js');
    console.log('üìÑ Created deployment package.json with ESM configuration');

  } catch (error) {
    console.error('‚ùå Build failed:', error);
    console.error(error.stack);
    process.exit(1);
  }
}

buildServer();

