#!/usr/bin/env node

/**
 * Enhanced deployment configuration for Replit
 * Handles ESM/CommonJS compatibility and module resolution issues
 */

import { build } from 'esbuild';
import { spawn } from 'child_process';
import { fileURLToPath } from 'url';
import { dirname, join } from 'path';
import { readFileSync, writeFileSync, mkdirSync, existsSync, rmSync } from 'fs';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
const rootDir = __dirname;

async function createDeploymentBuild() {
  try {
    console.log('üöÄ Creating deployment build with enhanced ESM compatibility...');

    // Clean and recreate dist directory
    const distDir = join(rootDir, 'dist');
    if (existsSync(distDir)) {
      rmSync(distDir, { recursive: true, force: true });
    }
    mkdirSync(distDir, { recursive: true });

    console.log('üì¶ Building client assets...');
    // Build client first
    await new Promise((resolve, reject) => {
      const clientProcess = spawn('npm', ['run', 'build:client'], {
        stdio: 'inherit',
        shell: true
      });

      clientProcess.on('close', (code) => {
        if (code === 0) resolve();
        else reject(new Error(`Client build failed with code ${code}`));
      });

      clientProcess.on('error', reject);
    });

    console.log('üîß Building server with advanced ESM configuration...');
    await build({
      entryPoints: [join(rootDir, 'server/index.ts')],
      bundle: true,
      platform: 'node',
      format: 'esm',
      outfile: join(distDir, 'index.js'),
      target: 'node18',
      sourcemap: true,
      minify: process.env.NODE_ENV === 'production',
      treeShaking: true,
      metafile: true,
      logLevel: 'info',
      mainFields: ['module', 'main'],
      conditions: ['node', 'import'],
      keepNames: true,
      legalComments: 'none',
      external: [
        // Node.js built-ins
        'fs', 'path', 'url', 'crypto', 'http', 'https', 'stream', 'util', 
        'events', 'buffer', 'os', 'child_process', 'querystring', 'net', 'tls',
        'zlib', 'assert', 'timers', 'module', 'cluster', 'worker_threads',

        // Core server dependencies
        '@neondatabase/serverless',
        'ws',
        'express',
        'passport',
        'passport-local',
        'passport-google-oauth20',
        'passport-microsoft',
        'passport-openid',
        'bcryptjs',
        'multer',
        'openai',
        'puppeteer',
        'drizzle-orm',
        'drizzle-kit',
        'zod',
        'zod-validation-error',
        'express-session',
        'connect-pg-simple',
        '@replit/database',
        '@replit/object-storage',
        'memorystore',
        'jspdf',
        'html2canvas',
        'memoizee',
        'date-fns',
        'openid-client',

        // Frontend packages that should never be in server bundle
        'react', 'react-dom', '@radix-ui/*', 'lucide-react', 'framer-motion',
        'react-hook-form', 'react-day-picker', 'recharts', 'embla-carousel-react',
        'react-resizable-panels', 'react-icons', '@tanstack/react-query',
        'vaul', 'wouter', 'next-themes', 'input-otp', 'cmdk',
        'class-variance-authority', 'clsx', 'tailwind-merge', 'tailwindcss-animate',
        'tw-animate-css',

        // Build and dev tools
        'vite', '@vitejs/plugin-react', 'autoprefixer', 'tailwindcss', 'postcss',
        'lightningcss', '@babel/preset-typescript', 'typescript', 'tsx', 'esbuild',
        '@types/*', '@replit/vite-plugin-cartographer', '@replit/vite-plugin-runtime-error-modal'
      ],
      define: {
        'process.env.NODE_ENV': JSON.stringify(process.env.NODE_ENV || 'production'),
        'import.meta.env.NODE_ENV': JSON.stringify(process.env.NODE_ENV || 'production'),
        'import.meta.env.PROD': JSON.stringify(true),
        'import.meta.env.DEV': JSON.stringify(false),
      },
      banner: {
        js: `
// Enhanced ESM compatibility and polyfills for deployment
import { createRequire } from 'module';
import { fileURLToPath } from 'url';
import { dirname } from 'path';

const require = createRequire(import.meta.url);
const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

// Global polyfills for packages expecting CommonJS environment
if (typeof globalThis !== 'undefined') {
  globalThis.__filename = __filename;
  globalThis.__dirname = __dirname;
  globalThis.require = require;
}

if (typeof global !== 'undefined') {
  global.__filename = __filename;
  global.__dirname = __dirname;
  global.require = require;
}

// Process environment polyfill
if (typeof process !== 'undefined') {
  process.env.NODE_ENV = process.env.NODE_ENV || 'production';
}

// Import.meta polyfill for compatibility
if (typeof import.meta === 'undefined') {
  globalThis.import = globalThis.import || {};
  globalThis.import.meta = globalThis.import.meta || { url: 'file://' + __filename };
}
`
      },
      loader: {
        '.json': 'json',
        '.txt': 'text',
        '.svg': 'text',
        '.png': 'file',
        '.jpg': 'file',
        '.jpeg': 'file',
        '.gif': 'file',
        '.webp': 'file'
      }
    });

    // Create deployment package.json with proper ESM configuration
    const originalPackageJson = JSON.parse(
      readFileSync(join(rootDir, 'package.json'), 'utf-8')
    );

    // Filter out dev dependencies and build tools from production dependencies
    const productionDeps = {};
    const excludePatterns = [
      '@types/', '@vitejs/', '@replit/vite-plugin', 'autoprefixer', 'tailwindcss',
      'postcss', 'typescript', 'tsx', 'vite', 'esbuild', 'drizzle-kit'
    ];

    Object.entries(originalPackageJson.dependencies || {}).forEach(([name, version]) => {
      if (!excludePatterns.some(pattern => name.startsWith(pattern))) {
        productionDeps[name] = version;
      }
    });

    const deploymentPackageJson = {
      name: originalPackageJson.name,
      version: originalPackageJson.version,
      type: "module",
      main: "index.js",
      engines: {
        node: ">=18.0.0"
      },
      scripts: {
        start: "node index.js"
      },
      dependencies: productionDeps
    };

    writeFileSync(
      join(distDir, 'package.json'),
      JSON.stringify(deploymentPackageJson, null, 2)
    );

    // Create a .replit file for deployment configuration
    const replitConfig = `[deployment]
run = ["node", "index.js"]
deploymentTarget = "cloudrun"

[[ports]]
localPort = 5000
externalPort = 80

[env]
NODE_ENV = "production"
`;

    writeFileSync(join(distDir, '.replit'), replitConfig);

    console.log('‚úÖ Deployment build completed successfully!');
    console.log('üìÅ Deployment files created:');
    console.log('  - dist/index.js (ESM server bundle)');
    console.log('  - dist/package.json (production dependencies)');
    console.log('  - dist/public/ (client assets)');
    console.log('  - dist/.replit (deployment configuration)');
    console.log('');
    console.log('üöÄ Ready for deployment! The build addresses:');
    console.log('  ‚úì ESM format with import.meta and top-level await support');
    console.log('  ‚úì Proper module resolution for external dependencies');
    console.log('  ‚úì CommonJS compatibility polyfills');
    console.log('  ‚úì Correct output file naming (index.js)');
    console.log('  ‚úì Production-optimized dependency list');

  } catch (error) {
    console.error('‚ùå Deployment build failed:', error);
    console.error(error.stack);
    process.exit(1);
  }
}

createDeploymentBuild();

