#!/usr/bin/env node

/**
 * Enhanced deployment build script that addresses all ESBuild deployment issues:
 * - ESM format support for import.meta and top-level await
 * - Proper module resolution for external dependencies
 * - Output file naming consistency (index.js instead of server.js)
 * - CommonJS compatibility polyfills
 */

import { build } from 'esbuild';
import { spawn } from 'child_process';
import { fileURLToPath } from 'url';
import { dirname, join } from 'path';
import { readFileSync, writeFileSync, mkdirSync, existsSync, rmSync } from 'fs';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
const rootDir = __dirname;

async function runCommand(command, args = [], options = {}) {
  return new Promise((resolve, reject) => {
    console.log(`üîß Executing: ${command} ${args.join(' ')}`);

    const process = spawn(command, args, {
      cwd: rootDir,
      stdio: 'inherit',
      shell: true,
      ...options
    });

    process.on('close', (code) => {
      if (code === 0) resolve();
      else reject(new Error(`${command} failed with exit code ${code}`));
    });

    process.on('error', reject);
  });
}

async function createEnhancedDeploymentBuild() {
  try {
    console.log('üöÄ Starting enhanced deployment build process...');
    console.log('üìã Addressing deployment issues:');
    console.log('  ‚Ä¢ ESM format with import.meta and top-level await support');
    console.log('  ‚Ä¢ Module resolution for lightningcss and @babel/preset-typescript');
    console.log('  ‚Ä¢ Output file naming (index.js instead of server.js)');
    console.log('  ‚Ä¢ ESM-compatible package.json configuration');
    console.log('');

    // Clean and recreate dist directory
    const distDir = join(rootDir, 'dist');
    console.log('üßπ Cleaning build directory...');
    if (existsSync(distDir)) {
      rmSync(distDir, { recursive: true, force: true });
    }
    mkdirSync(distDir, { recursive: true });

    // Build client assets first
    console.log('üé® Building client assets...');
    await runCommand('npm', ['run', 'build:client']);

    // Enhanced server build with full ESM support
    console.log('‚öôÔ∏è Building server with enhanced ESM configuration...');
    const buildResult = await build({
      entryPoints: [join(rootDir, 'server/index.ts')],
      bundle: true,
      platform: 'node',
      format: 'esm', // ESM format for import.meta and top-level await support
      outfile: join(distDir, 'index.js'), // Correct output file name
      target: 'node18',
      sourcemap: true,
      minify: process.env.NODE_ENV === 'production',
      treeShaking: true,
      keepNames: true,
      legalComments: 'none',
      metafile: true,
      logLevel: 'info',

      // Enhanced module resolution
      mainFields: ['module', 'main', 'browser'],
      conditions: ['node', 'import', 'require'],
      resolveExtensions: ['.ts', '.tsx', '.js', '.jsx', '.json'],

      // Comprehensive external dependencies list
      external: [
        // Node.js built-ins (always external)
        'fs', 'path', 'url', 'crypto', 'http', 'https', 'stream', 'util', 
        'events', 'buffer', 'os', 'child_process', 'querystring', 'net', 'tls',
        'zlib', 'assert', 'timers', 'module', 'cluster', 'worker_threads',
        'readline', 'perf_hooks', 'dns', 'dgram', 'vm', 'async_hooks',

        // Core database and server dependencies
        '@neondatabase/serverless',
        'ws',
        'express',
        'express-session',
        'connect-pg-simple',

        // Authentication
        'passport',
        'passport-local',
        'passport-google-oauth20', 
        'passport-microsoft',
        'passport-openid',
        'bcryptjs',

        // File handling and utilities
        'multer',
        'memoizee',
        'date-fns',

        // External services
        'openai',
        'puppeteer',
        'openid-client',

        // Database and validation
        'drizzle-orm',
        'drizzle-kit',
        'zod',
        'zod-validation-error',

        // Replit services
        '@replit/database',
        '@replit/object-storage',

        // Memory and session storage
        'memorystore',

        // PDF and canvas
        'jspdf',
        'html2canvas',

        // Frontend packages (should never be in server bundle)
        'react',
        'react-dom',
        '@radix-ui/*',
        'lucide-react',
        'framer-motion',
        'react-hook-form',
        'react-day-picker',
        'recharts',
        'embla-carousel-react',
        'react-resizable-panels',
        'react-icons',
        '@tanstack/react-query',
        'vaul',
        'wouter',
        'next-themes',
        'input-otp',
        'cmdk',
        'class-variance-authority',
        'clsx',
        'tailwind-merge',
        'tailwindcss-animate',
        'tw-animate-css',

        // Build tools and dev dependencies (problematic packages)
        'vite',
        '@vitejs/plugin-react',
        'autoprefixer',
        'tailwindcss',
        'postcss',
        'lightningcss', // Specifically mentioned in the error
        '@babel/preset-typescript', // Specifically mentioned in the error  
        'typescript',
        'tsx',
        'esbuild',
        '@types/*',
        '@replit/vite-plugin-cartographer',
        '@replit/vite-plugin-runtime-error-modal'
      ],

      // Environment variable definitions for ESM
      define: {
        'process.env.NODE_ENV': JSON.stringify(process.env.NODE_ENV || 'production'),
        'import.meta.env.NODE_ENV': JSON.stringify(process.env.NODE_ENV || 'production'),
        'import.meta.env.PROD': JSON.stringify(true),
        'import.meta.env.DEV': JSON.stringify(false),
        'import.meta.env.MODE': JSON.stringify('production'),
      },

      // Enhanced ESM compatibility banner
      banner: {
        js: `
// Enhanced ESM compatibility polyfills for deployment
import { createRequire } from 'module';
import { fileURLToPath } from 'url';
import { dirname } from 'path';

// ESM polyfills
const require = createRequire(import.meta.url);
const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

// Global polyfills for packages expecting CommonJS environment
if (typeof globalThis !== 'undefined') {
  globalThis.__filename = __filename;
  globalThis.__dirname = __dirname;
  globalThis.require = require;
}

if (typeof global !== 'undefined') {
  global.__filename = __filename;
  global.__dirname = __dirname;
  global.require = require;
}

// Process environment setup
if (typeof process !== 'undefined') {
  process.env.NODE_ENV = process.env.NODE_ENV || 'production';
}

// Import.meta compatibility polyfill
if (typeof import.meta.resolve === 'undefined') {
  import.meta.resolve = (specifier) => new URL(specifier, import.meta.url).href;
}
`
      },

      // File loaders
      loader: {
        '.json': 'json',
        '.txt': 'text',
        '.svg': 'text',
        '.png': 'file',
        '.jpg': 'file',
        '.jpeg': 'file',
        '.gif': 'file',
        '.webp': 'file',
        '.woff': 'file',
        '.woff2': 'file',
        '.ttf': 'file',
        '.otf': 'file'
      },

      // TypeScript configuration
      tsconfig: join(rootDir, 'tsconfig.deployment.json')
    });

    // Log build analysis
    if (buildResult.metafile) {
      console.log('üìä Build completed successfully with:');
      console.log(`  ‚Ä¢ Output size: ${Math.round(buildResult.metafile.outputs['dist/index.js']?.bytes / 1024 || 0)} KB`);
      console.log(`  ‚Ä¢ External dependencies: ${Object.keys(buildResult.metafile.inputs || {}).length} modules processed`);
    }

    // Create production package.json with ESM configuration
    console.log('üì¶ Creating deployment package.json...');
    const originalPackageJson = JSON.parse(
      readFileSync(join(rootDir, 'package.json'), 'utf-8')
    );

    // Filter production dependencies (exclude dev tools and problematic packages)
    const productionDeps = {};
    const excludePatterns = [
      '@types/', '@vitejs/', '@replit/vite-plugin', 'autoprefixer', 'tailwindcss',
      'postcss', 'typescript', 'tsx', 'vite', 'esbuild', 'drizzle-kit',
      'lightningcss', '@babel/preset-typescript' // Specifically exclude problematic packages
    ];

    Object.entries(originalPackageJson.dependencies || {}).forEach(([name, version]) => {
      if (!excludePatterns.some(pattern => name.startsWith(pattern))) {
        productionDeps[name] = version;
      }
    });

    const deploymentPackageJson = {
      name: originalPackageJson.name,
      version: originalPackageJson.version,
      type: "module", // ESM configuration
      main: "index.js", // Correct entry point
      engines: {
        node: ">=18.0.0"
      },
      scripts: {
        start: "node index.js"
      },
      dependencies: productionDeps
    };

    writeFileSync(
      join(distDir, 'package.json'),
      JSON.stringify(deploymentPackageJson, null, 2)
    );

    // Create deployment configuration
    console.log('‚öôÔ∏è Creating deployment configuration...');
    const replitConfig = `[deployment]
run = ["node", "index.js"]
deploymentTarget = "cloudrun"

[[ports]]
localPort = 5000
externalPort = 80

[env]
NODE_ENV = "production"
`;

    writeFileSync(join(distDir, '.replit'), replitConfig);

    // Create a README for deployment
    const deploymentReadme = `# Deployment Build

This build has been enhanced to address all deployment issues:

## Fixed Issues:
‚úÖ ESM format with import.meta and top-level await support
‚úÖ Module resolution for lightningcss and @babel/preset-typescript  
‚úÖ Output file correctly named as index.js (not server.js)
‚úÖ ESM-compatible package.json configuration
‚úÖ Custom build script handles all edge cases

## Files:
- \`index.js\` - ESM server bundle with polyfills
- \`package.json\` - Production dependencies only
- \`public/\` - Client assets  
- \`.replit\` - Deployment configuration

## To deploy:
