#!/usr/bin/env node

/**
 * Deployment Build Script - Complete ESM/CommonJS Compatibility Solution
 * Addresses all deployment issues with comprehensive fixes
 */

import { build } from 'esbuild';
import { execSync } from 'child_process';
import { fileURLToPath } from 'url';
import { dirname, join } from 'path';
import { readFileSync, writeFileSync, mkdirSync, existsSync } from 'fs';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
const rootDir = join(__dirname, '..');

async function deploymentBuild() {
  try {
    console.log('üöÄ Starting deployment build with comprehensive fixes...');

    // Ensure dist directory exists
    const distDir = join(rootDir, 'dist');
    if (!existsSync(distDir)) {
      mkdirSync(distDir, { recursive: true });
    }

    // Build client first
    console.log('üì¶ Building client...');
    execSync('npm run build:client', { 
      cwd: rootDir, 
      stdio: 'inherit',
      env: { ...process.env, NODE_ENV: 'production' }
    });

    // Build server with comprehensive ESM configuration
    console.log('üîß Building server with ESM format and deployment fixes...');

    await build({
      entryPoints: [join(rootDir, 'server/index.ts')],
      bundle: true,
      platform: 'node',
      format: 'esm', // ESM format to support import.meta and top-level await
      outfile: join(rootDir, 'dist/index.js'), // Correct output filename
      target: 'node18',
      sourcemap: true,
      minify: process.env.NODE_ENV === 'production',
      treeShaking: true,
      metafile: true,
      logLevel: 'info',
      external: [
        // Core Node.js modules
        'fs', 'path', 'url', 'crypto', 'http', 'https', 'stream', 'util', 
        'events', 'buffer', 'os', 'child_process', 'querystring', 'net', 'tls',

        // Database and storage
        '@neondatabase/serverless',
        'drizzle-orm',
        'drizzle-kit',
        '@replit/database',
        '@replit/object-storage',

        // Server dependencies
        'ws', 'express', 'passport', 'passport-local', 'passport-google-oauth20',
        'passport-microsoft', 'bcryptjs', 'multer', 'openai', 'puppeteer',
        'express-session', 'connect-pg-simple', 'memorystore',

        // Utilities
        'zod', 'zod-validation-error', 'memoizee', 'date-fns', 'jspdf', 'html2canvas',

        // Build tools and problematic packages (MUST BE EXTERNAL)
        'vite', '@vitejs/plugin-react', 'autoprefixer', 'tailwindcss', 'postcss',
        'lightningcss', '@babel/preset-typescript', '@replit/vite-plugin-cartographer',
        '@replit/vite-plugin-runtime-error-modal', '@tailwindcss/vite', '@tailwindcss/typography',

        // Frontend-only packages that should not be in server bundle
        'react', 'react-dom', '@radix-ui/*', 'lucide-react', 'framer-motion',
        'react-hook-form', 'react-day-picker', 'recharts', 'embla-carousel-react',
        'react-resizable-panels', 'react-icons', '@tanstack/react-query', 'vaul',
        'wouter', 'next-themes', 'input-otp', 'cmdk', 'class-variance-authority',
        'clsx', 'tailwind-merge', 'tailwindcss-animate', 'tw-animate-css',

        // Development tools
        'typescript', 'tsx', 'esbuild', '@types/*'
      ],
      define: {
        'process.env.NODE_ENV': JSON.stringify('production'),
        'import.meta.env.MODE': JSON.stringify('production'),
        'import.meta.env.PROD': 'true',
        'import.meta.env.DEV': 'false',
      },
      banner: {
        js: `
// ESM compatibility setup for Node.js deployment
import { createRequire } from 'module';
import { fileURLToPath } from 'url';
import { dirname } from 'path';

const require = createRequire(import.meta.url);
const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

// Polyfill globals for packages expecting CommonJS
if (typeof global !== 'undefined') {
  global.__filename = __filename;
  global.__dirname = __dirname;
  global.require = require;
}
`
      }
    });

    // Create deployment-ready package.json
    const originalPackageJson = JSON.parse(
      readFileSync(join(rootDir, 'package.json'), 'utf-8')
    );

    const deploymentPackageJson = {
      name: originalPackageJson.name,
      version: originalPackageJson.version,
      type: "module", // ESM module type
      main: "index.js",
      engines: {
        node: ">=18.0.0"
      },
      scripts: {
        start: "node index.js"
      },
      dependencies: originalPackageJson.dependencies
    };

    writeFileSync(
      join(distDir, 'package.json'),
      JSON.stringify(deploymentPackageJson, null, 2)
    );

    // Create deployment README
    const deploymentReadme = `# Deployment Package

This package contains the built application ready for deployment.

## Files:
- \`index.js\` - ESM-formatted server bundle
- \`package.json\` - Production dependencies with ESM configuration
- \`public/\` - Client assets (HTML, CSS, JS)

## Deployment:
The application is configured for ESM format with proper import.meta support.
Run with: \`node index.js\`

## Environment:
Requires Node.js 18+ and DATABASE_URL environment variable.
`;

    writeFileSync(join(distDir, 'README.md'), deploymentReadme);

    console.log('‚úÖ Deployment build completed successfully!');
    console.log('üìÅ Build output:');
    console.log('   - dist/index.js (ESM server bundle)');
    console.log('   - dist/package.json (ESM configuration)');
    console.log('   - dist/public/ (client assets)');
    console.log('   - dist/README.md (deployment guide)');
    console.log('');
    console.log('üöÄ Ready for deployment!');

  } catch (error) {
    console.error('‚ùå Deployment build failed:', error);
    console.error(error.stack);
    process.exit(1);
  }
}

deploymentBuild();

