// Memoized athlete dropdown component with search and infinite scroll
const AthleteDropdownList = memo(({ 
  open,
  setOpen,
  selectedSport,
  showEgyptianOnly,
  selectedAthleteId,
  onSelect
}: {
  open: boolean;
  setOpen: (open: boolean) => void;
  selectedSport: string;
  showEgyptianOnly: boolean;
  selectedAthleteId: string;
  onSelect: (id: string) => void;
}) => {
  const [searchInput, setSearchInput] = useState("");
  const [debouncedSearch, setDebouncedSearch] = useState("");
  const observerTarget = useRef<HTMLDivElement>(null);

  // Debounce search input
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedSearch(searchInput);
    }, 300);
    return () => clearTimeout(timer);
  }, [searchInput]);

  // Build query URL with parameters (excluding page)
  const baseQueryUrl = useMemo(() => {
    const params = new URLSearchParams();
    params.set('limit', '20');
    if (selectedSport) params.set('sport', selectedSport);
    if (showEgyptianOnly) params.set('nationality', 'Egypt');
    if (debouncedSearch) params.set('search', debouncedSearch);
    return `/api/athletes?${params.toString()}`;
  }, [selectedSport, showEgyptianOnly, debouncedSearch]);

  // Infinite query for athletes using shared authenticated infinite fetcher
  const {
    data,
    fetchNextPage,
    hasNextPage,
    isFetchingNextPage,
    isError,
    isLoading: isQueryLoading,
    isFetching,
  } = useInfiniteQuery<{ athletes: Athlete[]; total: number }>({
    queryKey: [baseQueryUrl, selectedSport, showEgyptianOnly, debouncedSearch],
    queryFn: getInfiniteQueryFn<{ athletes: Athlete[]; total: number }>({ on401: "throw" }),
    getNextPageParam: (lastPage, allPages) => {
      const currentCount = allPages.reduce((sum, page) => sum + (page.athletes?.length || 0), 0);
      const total = lastPage.total || 0;
      return currentCount < total ? allPages.length + 1 : undefined;
    },
    initialPageParam: 1,
  });

  // Flatten all pages into a single array
  const athletes = useMemo(() => {
    return data?.pages.flatMap(page => page.athletes || []) || [];
  }, [data]);

  // Check if we're waiting for debounce or loading
  const isSearching = searchInput !== debouncedSearch || isQueryLoading || isFetching;

  // Intersection Observer for infinite scroll
  useEffect(() => {
    const target = observerTarget.current;
    if (!target) return;

    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting && hasNextPage && !isFetchingNextPage) {
          fetchNextPage();
        }
      },
      { threshold: 0.1 }
    );

    observer.observe(target);
    return () => observer.disconnect();
  }, [hasNextPage, isFetchingNextPage, fetchNextPage]);

  const handleSelect = useCallback((athleteId: string) => {
    onSelect(athleteId);
    setOpen(false);
    setSearchInput("");
  }, [onSelect, setOpen]);

  return (
    <PopoverContent className="w-[400px] p-0" align="start">
      <Command shouldFilter={false}>
        <CommandInput 
          placeholder="Search athletes..." 
          value={searchInput}
          onValueChange={setSearchInput}
        />
        <CommandList>
          <ScrollArea className="h-[300px]">
            {isError && (
              <div className="p-4 text-sm text-destructive">
                Error loading athletes. Please try again.
              </div>
            )}
            {!isError && (
              <CommandGroup>
                {athletes.length === 0 && !isSearching && (
                  <CommandEmpty>No athlete found</CommandEmpty>
                )}
                {athletes.length === 0 && isSearching && (
                  <div className="flex items-center justify-center p-4">
                    <Loader2 className="h-4 w-4 animate-spin" />
                    <span className="ml-2 text-sm text-muted-foreground">Searching...</span>
                  </div>
                )}
                <div className="p-1">
                  {athletes.map((athlete: any) => {
                    const isSelected = selectedAthleteId === athlete.id.toString();
                    return (
                      <CommandItem
                        key={athlete.id}
                        value={athlete.id.toString()}
                        onSelect={() => handleSelect(athlete.id.toString())}
                        className="flex items-center gap-3 p-2 cursor-pointer"
                        data-testid={`athlete-item-${athlete.id}`}
                      >
                        <Avatar className="h-8 w-8 flex-shrink-0">
                          <AvatarImage src={athlete.profileImage} alt={athlete.name} />
                          <AvatarFallback className="bg-blue-100 dark:bg-blue-900/30">
                            <UserIcon className="h-4 w-4 text-blue-600 dark:text-blue-400" />
                          </AvatarFallback>
                        </Avatar>
                        <div className="flex-1 min-w-0">
                          <div className="font-medium truncate">{athlete.name}</div>
                          <div className="text-xs text-muted-foreground">
                            {getCountryFlagWithFallback(athlete.nationality)} {athlete.nationality}
                          </div>
                        </div>
                        <div className="flex gap-1 flex-shrink-0">
                          {athlete.worldRank && (
                            <Badge variant="outline" className="text-xs px-1 py-0">
                              W#{athlete.worldRank}
                            </Badge>
                          )}
                          {athlete.olympicRank && (
                            <Badge variant="outline" className="text-xs px-1 py-0">
                              O#{athlete.olympicRank}
                            </Badge>
                          )}
                        </div>
                      </CommandItem>
                    );
                  })}
                  {hasNextPage && (
                    <div ref={observerTarget} className="flex items-center justify-center p-2">
                      <Loader2 className="h-4 w-4 animate-spin" />
                      <span className="ml-2 text-xs text-muted-foreground">Loading more...</span>
                    </div>
                  )}
                </div>
              </CommandGroup>
            )}
          </ScrollArea>
        </CommandList>
      </Command>
    </PopoverContent>
  );
});

AthleteDropdownList.displayName = "AthleteDropdownList";

export default function RankUp() {
  const { t } = useLanguage();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { showEgyptianOnly } = useEgyptFilter();
  const { selectedSport } = useSport();

  const [selectedAthleteId, setSelectedAthleteId] = useState<string>("");
  const [targetRank, setTargetRank] = useState<string>("");
  const [rankingType, setRankingType] = useState<string>("");
  const [category, setCategory] = useState<string>("");
  const [targetDate, setTargetDate] = useState<string>("");
  const [rankUpResult, setRankUpResult] = useState<RankUpResult | null>(null);
  const [isCalculating, setIsCalculating] = useState(false);
  const [userChangedRankingType, setUserChangedRankingType] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  // Fetch selected athlete details for ranking data
  const { data: selectedAthlete } = useQuery({
    queryKey: ["/api/athletes", selectedAthleteId],
    queryFn: async () => {
      if (!selectedAthleteId) return null;
      const response = await fetch(`/api/athletes/${selectedAthleteId}`);
      if (!response.ok) throw new Error('Failed to fetch athlete');
      return response.json();
    },
    enabled: !!selectedAthleteId,
  });

  // Get available ranking types for selected athlete
  const availableRankingTypes = useMemo(() => {
    if (!selectedAthlete) return [];

    const types = [];
    if (selectedAthlete.worldRank) types.push({ value: "world", label: "World Rankings" });
    if (selectedAthlete.olympicRank) types.push({ value: "olympic", label: "Olympic Rankings" });

    return types;
  }, [selectedAthlete]);

  // Get categories specific to selected athlete and ranking type
  const categories = useMemo(() => {
    if (!selectedAthlete || !rankingType) return [];

    const cats = new Set<string>();
    if (rankingType === "world" && selectedAthlete.worldCategory) {
      cats.add(selectedAthlete.worldCategory);
    }
    if (rankingType === "olympic" && selectedAthlete.olympicCategory) {
      cats.add(selectedAthlete.olympicCategory);
    }

    return Array.from(cats).sort();
  }, [selectedAthlete, rankingType]);

  // Reset states when athlete changes (but not when just availableRankingTypes recalculates)
  useEffect(() => {
    setCategory("");
    setTargetRank("");
    setTargetDate("");
    if (availableRankingTypes.length > 0) {
      setRankingType(availableRankingTypes[0].value);
    } else {
      setRankingType("");
    }
  }, [selectedAthleteId]); // Only reset when athlete actually changes, not when ranking types recalculate

  // Handle when availableRankingTypes changes and current rankingType becomes invalid
  useEffect(() => {
    if (rankingType && availableRankingTypes.length > 0) {
      const isCurrentValid = availableRankingTypes.some(type => type.value === rankingType);
      if (!isCurrentValid) {
        setRankingType(availableRankingTypes[0].value);
        setCategory("");
        setTargetDate("");
      }
    }
  }, [availableRankingTypes, rankingType]);

  // Reset category only when ranking type changes (not when categories recalculate)
  useEffect(() => {
    if (!userChangedRankingType) {
      // Automatic ranking type change - clear category
      setCategory("");
    }
    setUserChangedRankingType(false);
  }, [rankingType]);

  // Only clear category if it becomes invalid (not on recalculation)
  const prevCategoriesRef = useRef<string[]>([]);
  useEffect(() => {
    const categoriesChanged = JSON.stringify(prevCategoriesRef.current) !== JSON.stringify(categories);
    if (categoriesChanged && category && categories.length > 0 && !categories.includes(category)) {
      setCategory("");
    }
    prevCategoriesRef.current = categories;
  }, [categories, category]);

  // Get current athlete's ranking for the selected ranking type
  const getCurrentRanking = (athlete: Athlete, type: string) => {
    if (type === 'world') return athlete.worldRank;
    if (type === 'olympic') return athlete.olympicRank;
    return null;
  };

  const selectedAthleteCurrentRank = selectedAthlete ? getCurrentRanking(selectedAthlete, rankingType) : null;
  const isAlreadyTopRank = selectedAthleteCurrentRank === 1;

  // Calculate rank up requirements
  const calculateRankUp = async () => {
    if (!selectedAthleteId || !rankingType || !category) {
      toast({
        title: "Missing Information",
        description: "Please select athlete, ranking type and category",
        variant: "destructive",
      });
      return;
    }

    // For top rank athletes, set target to maintain rank 1
    let effectiveTargetRank = targetRank;
    if (isAlreadyTopRank) {
      effectiveTargetRank = "1"; // Maintenance mode
    } else {
      if (!targetRank) {
        toast({
          title: "Missing Target Rank",
          description: "Please enter a target rank",
          variant: "destructive",
        });
        return;
      }

      const targetRankNum = parseInt(targetRank);
      if (selectedAthleteCurrentRank && targetRankNum >= selectedAthleteCurrentRank) {
        toast({
          title: "Invalid Target Rank",
          description: `Target rank must be better (lower) than current rank #${selectedAthleteCurrentRank}`,
          variant: "destructive",
        });
        return;
      }
    }

    setIsCalculating(true);
    try {
      const response = await fetch("/api/rank-up/calculate", {
        method: "POST",
        body: JSON.stringify({
          athleteId: parseInt(selectedAthleteId),
          targetRank: parseInt(effectiveTargetRank),
          rankingType,
          category,
          targetDate: targetDate || null,
        }),
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      setRankUpResult(await response.json());
      toast({
        title: "Calculation Complete",
        description: isAlreadyTopRank 
          ? "Rank maintenance strategy generated successfully"
          : "Rank up requirements calculated successfully",
      });
    } catch (error) {
      console.error("Calculation error:", error);
      toast({
        title: "Calculation Failed",
        description: "Failed to calculate rank up requirements",
        variant: "destructive",
      });
    } finally {
      setIsCalculating(false);
    }
  };

  const formatDate = (dateStr: string) => {
    return new Date(dateStr).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800">
      <Header title="Rank Up Calculator" description="Find the competitions you need to advance your ranking" />

      <main className="container mx-auto px-4 py-8">
        <div className="max-w-7xl mx-auto">
          {/* Header Section */}
          <div className="mb-8">
            <div className="flex items-center gap-3 mb-4">
              <div className="p-3 rounded-full bg-blue-100 dark:bg-blue-900">
                <Target className="h-8 w-8 text-blue-600 dark:text-blue-400" />
              </div>
              <div>
                <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
                  Rank Up Calculator
                </h1>
                <p className="text-gray-600 dark:text-gray-300">
                  Find the competitions you need to advance your ranking
                </p>
              </div>
            </div>
          </div>

          {/* Input Form */}
          <Card className="mb-8">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Calculator className="h-5 w-5" />
                Calculate Rank Up Requirements
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-5 gap-6">
                <div className="space-y-2">
                  <Label htmlFor="athlete">Select Athlete</Label>
                  <Popover open={dropdownOpen} onOpenChange={setDropdownOpen}>
                    <PopoverTrigger asChild>
                      <Button
                        variant="outline"
                        role="combobox"
                        aria-expanded={dropdownOpen}
                        className="w-full justify-between"
                        data-testid="button-select-athlete"
                      >
                        {selectedAthlete ? (
                          <div className="flex items-center gap-2 min-w-0">
                            <Avatar className="h-6 w-6 flex-shrink-0">
                              <AvatarImage src={selectedAthlete.profileImage} alt={selectedAthlete.name} />
                              <AvatarFallback className="bg-blue-100 dark:bg-blue-900/30">
                                <UserIcon className="h-3 w-3 text-blue-600 dark:text-blue-400" />
                              </AvatarFallback>
                            </Avatar>
                            <span className="truncate">{selectedAthlete.name}</span>
                          </div>
                        ) : (
                          "Choose athlete..."
                        )}
                        <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
                      </Button>
                    </PopoverTrigger>
                    <AthleteDropdownList
                      open={dropdownOpen}
                      setOpen={setDropdownOpen}
                      selectedSport={selectedSport}
                      showEgyptianOnly={showEgyptianOnly}
                      selectedAthleteId={selectedAthleteId}
                      onSelect={setSelectedAthleteId}
                    />
                  </Popover>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="targetRank">
                    Target Rank
                    {selectedAthleteCurrentRank && (
                      <span className="text-sm font-normal text-gray-500 ml-2">
                        (Current: #{selectedAthleteCurrentRank})
                      </span>
                    )}
                  </Label>
                  {isAlreadyTopRank ? (
                    <div className="flex items-center gap-2 p-3 bg-blue-50 border border-blue-200 rounded-md">
                      <Trophy className="h-4 w-4 text-blue-600" />
                      <span className="text-sm text-blue-700">
                        Currently Rank #1 - Get AI strategy to maintain your top position!
                      </span>
                    </div>
                  ) : (
                    <Input
                      id="targetRank"
                      type="number"
                      placeholder={selectedAthleteCurrentRank ? `1-${selectedAthleteCurrentRank - 1}` : "e.g., 1"}
                      value={targetRank}
                      onChange={(e) => setTargetRank(e.target.value)}
                      min="1"
                      max={selectedAthleteCurrentRank ? selectedAthleteCurrentRank - 1 : undefined}
                    />
                  )}
                </div>

                <div className="space-y-2">
                  <Label htmlFor="rankingType">Ranking Type</Label>
                  <Select value={rankingType || undefined} onValueChange={(value) => {
                    setUserChangedRankingType(true);
                    setRankingType(value);
                  }}>
                    <SelectTrigger>
                      <SelectValue placeholder={availableRankingTypes.length === 0 ? "Select athlete first" : "Select ranking type"} />
                    </SelectTrigger>
                    <SelectContent>
                      {availableRankingTypes.length === 0 ? (
                        <SelectItem value="no-data" disabled>
                          No ranking data available
                        </SelectItem>
                      ) : (
                        availableRankingTypes.map((type) => (
                          <SelectItem key={type.value} value={type.value}>
                            {type.label}
                          </SelectItem>
                        ))
                      )}
                    </SelectContent>
                  </Select>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="category">Weight Category</Label>
                  <Select value={category} onValueChange={setCategory}>
                    <SelectTrigger>
                      <SelectValue placeholder={categories.length === 0 ? (selectedAthleteId ? "No categories available" : "Select athlete first") : "Select category..."} />
                    </SelectTrigger>
                    <SelectContent>
                      {categories.length === 0 ? (
                        <SelectItem value="no-categories" disabled>
                          {selectedAthleteId ? "No weight categories found for this athlete" : "Select an athlete first"}
                        </SelectItem>
                      ) : (
                        categories.map((cat) => (
                          <SelectItem key={cat} value={cat}>
                            {cat}
                          </SelectItem>
                        ))
                      )}
                    </SelectContent>
                  </Select>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="targetDate">Target Date (Optional)</Label>
                  <Input
                    id="targetDate"
                    type="date"
                    value={targetDate}
                    onChange={(e) => setTargetDate(e.target.value)}
                    min={new Date().toISOString().split('T')[0]}
                    data-testid="input-target-date"
                  />
                  <p className="text-xs text-gray-500 dark:text-gray-400">
                    When do you want to reach your target rank?
                  </p>
                </div>
              </div>

              <div className="pt-4">
                <Button 
                onClick={calculateRankUp}
                disabled={isCalculating || !selectedAthleteId || !rankingType || !category || (!isAlreadyTopRank && !targetRank)}
                className="w-full md:w-auto px-8 py-2"
              >
                {isCalculating ? "Calculating..." : isAlreadyTopRank ? "Get Maintenance Strategy" : "Calculate Requirements"}
                </Button>
              </div>
            </CardContent>
          </Card>

          {/* Results Section */}
          {rankUpResult && (
            <div className="space-y-6">
              {/* Overview Card */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <TrendingUp className="h-5 w-5" />
                    Rank Up Analysis
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
                    <div className="text-center">
                      <div className="text-2xl font-bold text-blue-600 dark:text-blue-400">
                        #{rankUpResult.currentRank}
                      </div>
                      <div className="text-sm text-gray-600 dark:text-gray-400">Current Rank</div>
                    </div>
                    <div className="text-center">
                      <div className="text-2xl font-bold text-green-600 dark:text-green-400">
                        {rankUpResult.currentPoints.toFixed(2)}
                      </div>
                      <div className="text-sm text-gray-600 dark:text-gray-400">Current Points</div>
                    </div>
                    <div className="text-center">
                      <div className="text-2xl font-bold text-orange-600 dark:text-orange-400">
                        {rankUpResult.pointsNeeded.toFixed(2)}
                      </div>
                      <div className="text-sm text-gray-600 dark:text-gray-400">Points Needed</div>
                    </div>
                    <div className="text-center">
                      <div className="text-2xl font-bold text-purple-600 dark:text-purple-400">
                        {(rankUpResult.targetPoints + 10).toFixed(2)}
                      </div>
                      <div className="text-sm text-gray-600 dark:text-gray-400">Total Required</div>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Suggested Competitions */}
              {rankUpResult.suggestedCompetitions.length > 0 && (
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <Trophy className="h-5 w-5" />
                      Suggested Competitions
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="overflow-x-auto">
                      <table className="w-full border-collapse">
                        <thead>
                          <tr className="border-b">
                            <th className="text-left p-3 font-semibold">Competition Name</th>
                            <th className="text-left p-3 font-semibold">Country/Region</th>
                            <th className="text-left p-3 font-semibold">Date</th>
                            <th className="text-center p-3 font-semibold">Cumulative Points</th>
                          </tr>
                        </thead>
                        <tbody>
                          {rankUpResult.suggestedCompetitions.map((comp) => (
                            <tr key={comp.id} className="border-b hover:bg-gray-50 dark:hover:bg-gray-800">
                              <td className="p-3">
                                <div className="flex items-center gap-2">
                                  <Medal className="h-4 w-4 text-gold" />
                                  <div>
                                    <div className="font-medium">{comp.name}</div>
                                    {comp.gradeLevel && (
                                      <Badge variant="secondary" className="text-xs mt-1">
                                        {comp.gradeLevel}
                                      </Badge>
                                    )}
                                  </div>
                                </div>
                              </td>
                              <td className="p-3">
                                <div className="flex items-center gap-2">
                                  <MapPin className="h-4 w-4 text-gray-500" />
                                  <div>
                                    <div>{comp.country}</div>
                                    {comp.city && (
                                      <div className="text-sm text-gray-500">{comp.city}</div>
                                    )}
                                  </div>
                                </div>
                              </td>
                              <td className="p-3">
                                <div className="flex items-center gap-2">
                                  <Calendar className="h-4 w-4 text-gray-500" />
                                  <div>
                                    <div>{formatDate(comp.startDate)}</div>
                                    {comp.endDate && (
                                      <div className="text-sm text-gray-500">
                                        to {formatDate(comp.endDate)}
                                      </div>
                                    )}
                                  </div>
                                </div>
                              </td>
                              <td className="p-3 text-center">
                                <div className="font-semibold text-blue-600 dark:text-blue-400">
                                  {comp.cumulativePoints ?? 0} pts
                                </div>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </CardContent>
                </Card>
              )}

              {/* AI Recommendations */}
              {rankUpResult.aiRecommendations && (
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                      <Brain className="h-5 w-5" />
                      {rankUpResult.currentRank === 1 
                        ? "AI Rank Maintenance Strategy"
                        : "AI Strategic Recommendations"}
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-6">
                    {/* Strategy Overview */}
                    <div>
                      <h4 className="font-semibold mb-2 text-blue-600 dark:text-blue-400">Strategy Overview</h4>
                      <p className="text-gray-700 dark:text-gray-300">{rankUpResult.aiRecommendations.strategy}</p>
                    </div>

                    {/* Priority Competitions */}
                    {rankUpResult.aiRecommendations.priorityCompetitions && rankUpResult.aiRecommendations.priorityCompetitions.length > 0 && (
                      <div>
                        <h4 className="font-semibold mb-3 text-green-600 dark:text-green-400">Priority Competitions</h4>
                        <div className="space-y-3">
                          {rankUpResult.aiRecommendations.priorityCompetitions.map((comp, index) => (
                            <div key={index} className="border rounded-lg p-4 bg-gray-50 dark:bg-gray-800">
                              <div className="flex justify-between items-start mb-2">
                                <h5 className="font-medium text-gray-900 dark:text-gray-100">{comp.name}</h5>
                                <Badge variant="outline" className="text-xs">
                                  {comp.points} points
                                </Badge>
                              </div>
                              <div className="flex items-center gap-2 mb-2">
                                <Calendar className="h-4 w-4 text-gray-500" />
                                <span className="text-sm text-gray-600 dark:text-gray-400">
                                  {formatDate(comp.startDate)}
                                  {comp.endDate && comp.endDate !== comp.startDate && (
                                    <> - {formatDate(comp.endDate)}</>
                                  )}
                                </span>
                              </div>
                              <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">{comp.reasoning}</p>
                              <div className="text-sm text-purple-600 dark:text-purple-400">
                                <strong>Required Performance:</strong> {comp.rank_needed}
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* Grid for Timeline and Risk */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <div>
                        <h4 className="font-semibold mb-2 text-orange-600 dark:text-orange-400">
                          {rankUpResult.currentRank === 1 
                            ? "Maintenance Timeline" 
                            : "Timeline to Target"}
                        </h4>
                        <p className="text-gray-700 dark:text-gray-300">{rankUpResult.aiRecommendations.timelineToTarget}</p>
                      </div>
                      <div>
                        <h4 className="font-semibold mb-2 text-red-600 dark:text-red-400">Risk Assessment</h4>
                        <p className="text-gray-700 dark:text-gray-300">{rankUpResult.aiRecommendations.riskAssessment}</p>
                      </div>
                    </div>

                    {/* Alternative Strategies */}
                    {rankUpResult.aiRecommendations.alternativeStrategies && rankUpResult.aiRecommendations.alternativeStrategies.length > 0 && (
                      <div>
                        <h4 className="font-semibold mb-3 text-purple-600 dark:text-purple-400">Alternative Strategies</h4>
                        <ul className="space-y-2">
                          {rankUpResult.aiRecommendations.alternativeStrategies.map((strategy, index) => (
                            <li key={index} className="flex items-start gap-2">
                              <div className="w-2 h-2 rounded-full bg-purple-500 mt-2 flex-shrink-0"></div>
                              <span className="text-gray-700 dark:text-gray-300">{strategy}</span>
                            </li>
                          ))}
                        </ul>
                      </div>
                    )}

                    {/* Points Summary */}
                    <div className="bg-blue-50 dark:bg-blue-900/20 rounded-lg p-4">
                      <div className="flex items-center justify-between">
                        <span className="font-medium text-blue-800 dark:text-blue-200">
                          Total Points from AI Recommendations:
                        </span>
                        <span className="font-bold text-blue-600 dark:text-blue-400">
                          {rankUpResult.aiRecommendations.totalPointsFromRecommendations} points
                        </span>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              )}
            </div>
          )}

          {/* Information Card */}
          {!rankUpResult && (
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Target className="h-5 w-5" />
                  How Rank Up Works
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <h4 className="font-semibold mb-2">Calculation Method</h4>
                    <ul className="space-y-1 text-sm text-gray-600 dark:text-gray-400">
                      <li>‚Ä¢ Analyzes your current ranking and points</li>
                      <li>‚Ä¢ Finds the athlete at your target rank</li>
                      <li>‚Ä¢ Calculates points needed to exceed by 10+ points</li>
                      <li>‚Ä¢ Suggests optimal competition combinations</li>
                    </ul>
                  </div>
                  <div>
                    <h4 className="font-semibold mb-2">Competition Selection</h4>
                    <ul className="space-y-1 text-sm text-gray-600 dark:text-gray-400">
                      <li>‚Ä¢ Prioritizes higher point-value competitions</li>
                      <li>‚Ä¢ Shows upcoming competitions only</li>
                      <li>‚Ä¢ Matches your weight category</li>
                      <li>‚Ä¢ Considers geographic proximity</li>
                    </ul>
                  </div>
                </div>
              </CardContent>
            </Card>
          )}
        </div>
      </main>
    </div>
  );
}

server/openai-service.ts.content:
import type { Competition } from "@shared/schema";
import { getRealisticPointsProjection, parseCompetitionType, getAvailableGRankingLevels } from "./g-ranking-calculator";
import { getOpenAIClient } from "./openai-client";

export interface CompetitionRecommendation {
  strategy: string;
  priorityCompetitions: {
    competitionId: number;
    name: string;
    points: number;
    reasoning: string;
    rank_needed: string;
    startDate: string;
    endDate: string;
  }[];
  totalPointsFromRecommendations: number;
  timelineToTarget: string;
  riskAssessment: string;
  alternativeStrategies: string[];
}

export async function getCompetitionRecommendations(
  upcomingCompetitions: Competition[],
  pointsNeeded: number,
  currentRank: number,
  targetRank: number,
  category: string,
  rankingType: string,
  targetDate?: string,
): Promise<CompetitionRecommendation> {
  const openai = getOpenAIClient();
  if (!openai) {
    throw new Error("OpenAI API key not configured. AI recommendations are unavailable.");
  }

  try {
    // Check if athlete is already at top rank and wants to maintain it
    const isTopRank = currentRank === 1;
    const isMaintaining =
      isTopRank && (targetRank === 1 || targetRank === currentRank);

    const prompt = `
You are a sports performance strategist specializing in competitive ranking optimization. Analyze the following data and provide strategic recommendations for an athlete.

ATHLETE CONTEXT:
- Current Rank: ${currentRank}
- Target Rank: ${targetRank} 
- Category: ${category}
- Ranking Type: ${rankingType}
- Points Needed: ${pointsNeeded}${
      targetDate
        ? `
- Target Achievement Date: ${targetDate}`
        : ""
    }

UPCOMING COMPETITIONS:
${upcomingCompetitions
  .map(
    (comp) => {
      const { isGrandPrixChallenge, isGrandPrixFinal } = parseCompetitionType(comp.name);
      const gradeLevel = comp.gradeLevel || 'Unknown';
      const pointsProjection = gradeLevel !== 'Unknown' ? 
        getRealisticPointsProjection(gradeLevel, currentRank, 'moderate', isGrandPrixChallenge, isGrandPrixFinal) :
        { optimistic: 0, realistic: 0, conservative: 0 };

      return `
- Competition: ${comp.name}
- Location: ${comp.city}, ${comp.country}
- Start Date: ${comp.startDate}
- End Date: ${comp.endDate || comp.startDate}
- G-Ranking Level: ${gradeLevel}${isGrandPrixChallenge ? ' (Grand Prix Challenge)' : ''}${isGrandPrixFinal ? ' (Grand Prix Final)' : ''}
- Competition Level: ${comp.competitionType}
- Realistic Points Projection: Optimistic ${pointsProjection.optimistic}, Realistic ${pointsProjection.realistic}, Conservative ${pointsProjection.conservative}
- Registration Deadline: ${comp.registrationDeadline}
`;
    }
  )
  .join("\n")}

SCORING SYSTEM (G-RANKING SYSTEM):
Points are awarded based on the competition's G-ranking level and athlete placement. The system includes:

G-RANKING LEVELS AND 1ST PLACE POINTS:
- G1: 10.00 points (Regional/smaller international)
- G2: 20.00 points (Standard international)
- G2*: 20.00 points (Grand Prix Challenge only)
- G3: 30.00 points (Major international)
- G4: 40.00 points (Continental level)
- G6: 50.00 points (Major continental)
- G10**: 100.00 points (Grand Prix Final only)
- G14: 140.00 points (World Championships, Olympics)

POINTS BY PLACEMENT (example for G14):
- 1st place: 140.00 points (100%)
- 2nd place: 84.00 points (~60%)
- 3rd place: 50.40 points (~36%)
- 5th place: 30.24 points (~22%)
- 9th place: 21.17 points (~15%)
- 17th place: 14.82 points (~11%)
- 33rd place: 10.37 points (~7%)
- 65th place: 7.26 points (~5%)

Lower G-ranking competitions have proportionally lower points for each placement.

IMPORTANT: Each competition has been analyzed for realistic point projections based on the athlete's current ranking. Use these projections rather than maximum points when making recommendations.

TASK:
${
  isMaintaining
    ? `This athlete is currently RANK #1 and wants to MAINTAIN their top position. Analyze these competitions and create an optimal defensive strategy to protect and strengthen their #1 ranking${targetDate ? ` within the target timeline ending ${targetDate}` : ""}. Focus on:
1. Strategic competition selection to maintain dominance and visibility
2. Building a significant point buffer against rising competitors
3. Maintaining competitive momentum without overexposure
4. Managing competition schedule for peak performance timing
5. Risk management to protect current ranking from unexpected challengers
6. Travel efficiency - group competitions by geographic regions to minimize travel fatigue and costs
7. Timeline optimization to maintain peak form${targetDate ? ` leading up to ${targetDate}` : ""}`
    : `Analyze these competitions and create an optimal strategy for the athlete to collect the needed ${pointsNeeded} points to reach rank ${targetRank}${targetDate ? ` by the target date of ${targetDate}` : ""}. Consider:
1. ${targetDate ? `TIME CONSTRAINT: All recommended competitions must occur BEFORE ${targetDate} to meet the deadline` : "Competition timing and scheduling flexibility"}
2. Point efficiency analysis - calculate points per competition ratio
3. Competition difficulty assessment and realistic medal placement expectations
4. Registration deadlines and administrative requirements
5. TRAVEL OPTIMIZATION (Critical Priority):
   - Group competitions by continent/region (e.g., all European competitions in one trip)
   - Minimize intercontinental travel - prefer regional circuits
   - Consider travel time, jet lag recovery periods (minimum 3-5 days between distant competitions)
   - Account for visa requirements and processing times for international competitions
   - Prioritize competitions accessible within 2-3 connected trips rather than scattered individual events
6. Cost-benefit analysis including travel expenses vs. point potential
7. Recovery time between competitions (minimum 1-2 weeks for optimal performance)
8. Peak performance timing - schedule most important competitions when athlete will be in best form`
}

Provide your response in JSON format with the following structure:
{
  "strategy": "${
    isMaintaining
      ? "Overall defensive strategy to maintain #1 ranking"
      : "Overall strategic approach description"
  }",
  "priorityCompetitions": [
    {
      "competitionId": number,
      "name": "competition name",
      "points": number,
      "reasoning": "${
        isMaintaining
          ? "why this competition helps maintain #1 position"
          : "why this competition is recommended"
      }",
      "rank_needed": "estimated rank needed to earn these points",
      "startDate": "YYYY-MM-DD format",
      "endDate": "YYYY-MM-DD format"
    }
  ],
  "totalPointsFromRecommendations": number,
  "timelineToTarget": "${
    isMaintaining
      ? "timeline for maintaining dominance"
      : "realistic timeline to achieve target rank"
  }",
  "riskAssessment": "${
    isMaintaining
      ? "risks to current #1 position and mitigation strategies"
      : "potential risks and challenges"
  }",
  "alternativeStrategies": ["${
    isMaintaining
      ? "backup strategies to protect ranking if competitors advance"
      : "backup strategies if primary plan fails"
  }"]
}
`;

    console.log("=== SENDING PROMPT TO OPENAI ===");
    console.log("Target Date:", targetDate || "None specified");
    console.log("Full Prompt:");
    console.log(prompt);
    console.log("=== END PROMPT ===");
    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [
        {
          role: "system",
          content:
            "You are an expert sports strategist. Provide detailed, actionable recommendations in the exact JSON format requested. Be specific about competition rankings and point calculations.",
        },
        {
          role: "user",
          content: prompt,
        },
      ],
      response_format: { type: "json_object" },
    });

    // Validate response structure
    if (!response.choices || response.choices.length === 0) {
      throw new Error("No response received from OpenAI");
    }

    const content = response.choices[0].message.content;
    if (!content) {
      throw new Error("Empty response from OpenAI");
    }

    const result = JSON.parse(content);

    return {
      strategy: result.strategy || "No strategy provided",
      priorityCompetitions: Array.isArray(result.priorityCompetitions)
        ? result.priorityCompetitions
        : [],
      totalPointsFromRecommendations:
        Number(result.totalPointsFromRecommendations) || 0,
      timelineToTarget: result.timelineToTarget || "Timeline not specified",
      riskAssessment: result.riskAssessment || "No risk assessment provided",
      alternativeStrategies: Array.isArray(result.alternativeStrategies)
        ? result.alternativeStrategies
        : [],
    };
  } catch (error) {
    console.error("OpenAI API error:", error);
    throw new Error(
      "Failed to get AI recommendations: " + (error as Error).message,
    );
  }
}

client/src/components/sport-switcher.tsx.content:
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useSport, type SportType, getSportConfig } from "@/lib/sport-context";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import { Trophy, Target } from "lucide-react";

export default function SportSwitcher() {
  const { selectedSport, setSelectedSport } = useSport();
  const { toast } = useToast();

  const handleSportChange = (newSport: SportType) => {
    if (newSport === selectedSport) return;

    setSelectedSport(newSport);

    // Invalidate all queries to refresh data for the new sport
    queryClient.invalidateQueries();

    const sportConfig = getSportConfig(newSport);
    toast({
      title: "Sport Changed",
      description: `Switched to ${sportConfig.name}. Data is now filtered for ${sportConfig.name} athletes.`,
    });
  };

  const currentSportConfig = getSportConfig(selectedSport);

  return (
    <div className="flex items-center space-x-2 px-3 py-1 bg-gradient-to-r from-gray-50 to-gray-100 dark:from-gray-800 dark:to-gray-700 rounded-lg border border-gray-200 dark:border-gray-600">
      <div className={`p-1 rounded ${selectedSport === 'taekwondo' ? 'text-blue-600 dark:text-blue-400' : 'text-red-600 dark:text-red-400'}`}>
        {selectedSport === 'taekwondo' ? <Trophy className="h-4 w-4" /> : <Target className="h-4 w-4" />}
      </div>
      <Select value={selectedSport} onValueChange={handleSportChange}>
        <SelectTrigger className="border-none bg-transparent h-8 w-[140px] text-sm font-medium focus:ring-0 focus:ring-offset-0" data-testid="sport-selector">
          <SelectValue>
            {currentSportConfig.name}
          </SelectValue>
        </SelectTrigger>
        <SelectContent>
          <SelectItem value="taekwondo" data-testid="sport-option-taekwondo">
            <div className="flex items-center space-x-2">
              <Trophy className="h-4 w-4 text-blue-600 dark:text-blue-400" />
              <span>Taekwondo</span>
            </div>
          </SelectItem>
          <SelectItem value="karate" data-testid="sport-option-karate">
            <div className="flex items-center space-x-2">
              <Target className="h-4 w-4 text-red-600 dark:text-red-400" />
              <span>Karate</span>
            </div>
          </SelectItem>
        </SelectContent>
      </Select>
    </div>
  );
}

