  userId: string;
  competitionId: number;
  competitionName: string;
  competitionType: string;
  location: string;
  dateRange: string;
  isSelected: boolean;
}

interface CompetitionWithParticipants extends Competition {
  participantCount: number;
  participants?: (CompetitionParticipant & { athlete: Athlete })[];
}

interface ParticipantData {
  weightCategory: string;
  athleteCount: number;
  athletes: {
    name: string;
    license: string;
    country: string;
    club: string;
    avatar: string;
    organization: string;
  }[];
}

export default function CompetitionDraws() {
  const [selectedCompetition, setSelectedCompetition] = useState<CompetitionWithParticipants | null>(null);
  const [drawSheetOpen, setDrawSheetOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("upcoming");
  const [divisionsSearchTerm, setDivisionsSearchTerm] = useState("");

  // Fetch current user
  const { data: user } = useQuery<{
    id: string;
    email?: string;
    firstName?: string;
    lastName?: string;
  }>({
    queryKey: ["/api/auth/user"],
  });

  // Fetch user's competition preferences
  const { data: userPreferences = [] } = useQuery<CompetitionPreference[]>({
    queryKey: [`/api/competition-preferences/${user?.id}`],
    enabled: !!user?.id,
  });

  // Fetch all competitions with participant counts
  const { data: allCompetitions, isLoading } = useQuery<CompetitionWithParticipants[]>({
    queryKey: ['/api/competitions-with-participants'],
  });

  // Filter competitions to show only upcoming ones from user preferences
  const competitions = allCompetitions?.filter(comp => {
    // Only show competitions if user has preferences and has explicitly selected this one
    if (!userPreferences.length) {
      return false; // No preferences = no competitions shown
    }

    // Check if user has selected this competition
    const userPreference = userPreferences.find(pref => pref.competitionId === comp.id);
    const isSelectedByUser = userPreference?.isSelected === true;

    // Check if competition is upcoming
    const isUpcoming = comp.status === 'upcoming';

    return isSelectedByUser && isUpcoming;
  }) || [];

  // Fetch competition participants from local database when a competition is selected
  const { data: localParticipants, isLoading: localParticipantsLoading } = useQuery<(CompetitionParticipant & { athlete: Athlete })[]>({
    queryKey: [`/api/competitions/${selectedCompetition?.id}/participants`],
    enabled: !!selectedCompetition?.id,
  });

  // Fetch participants from SimplyCompete API when a competition is selected
  const { data: liveParticipants, isLoading: liveParticipantsLoading, error: liveParticipantsError } = useQuery<ParticipantData[]>({
    queryKey: [`/api/competitions/${selectedCompetition?.id}/participants-live`],
    enabled: !!selectedCompetition?.id && drawSheetOpen,
  });

  const filteredCompetitions = competitions?.filter(comp => {
    const matchesSearch = comp.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         comp.country.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         comp.city?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === "all" || comp.status === statusFilter;
    return matchesSearch && matchesStatus;
  }) || [];

  const handleViewDrawSheet = (competition: CompetitionWithParticipants) => {
    setSelectedCompetition(competition);
    setDrawSheetOpen(true);
  };

  // Filter participants based on search term
  const filteredParticipants = liveParticipants?.filter(participant =>
    participant.weightCategory?.toLowerCase().includes(divisionsSearchTerm.toLowerCase())
  ) || [];

  const handleExportCSV = () => {
    if (!liveParticipants || !selectedCompetition) return;

    const csvContent = [
      ["Weight Category", "Athlete Name", "License", "Country", "Club"],
      ...filteredParticipants.flatMap(participant => 
        participant.athletes.map(athlete => [
          participant.weightCategory,
          athlete.name,
          athlete.license,
          athlete.country,
          athlete.club || ''
        ])
      )
    ].map(row => row.join(",")).join("\n");

    const blob = new Blob([csvContent], { type: "text/csv" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `${selectedCompetition.name}_participants.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'upcoming':
        return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      case 'ongoing':
        return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'completed':
        return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200';
      case 'cancelled':
        return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200';
      default:
        return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200';
    }
  };

  if (isLoading) {
    return (
      <>
        <Header 
          title="Drawsheet" 
          description="View upcoming competitions and generate tournament draw sheets"
        />
        <div className="p-6">
          <div className="animate-pulse space-y-4">
            {[1, 2, 3, 4].map((i) => (
              <div key={i} className="h-32 bg-gray-200 rounded-lg"></div>
            ))}
          </div>
        </div>
      </>
    );
  }

  return (
    <>
      <Header 
        title="Drawsheet" 
        description="View upcoming competitions and generate tournament draw sheets"
      />
      <div className="p-6 space-y-6">
        {/* Search and Filter */}
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
            <Input
              placeholder="Search competitions by name, country, or city..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
              data-testid="input-search-competitions"
            />
          </div>
          <div className="flex items-center space-x-2">
            <Filter className="w-4 h-4 text-gray-500" />
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded-md text-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              data-testid="select-status-filter"
            >
              <option value="all">All Status</option>
              <option value="upcoming">Upcoming</option>
              <option value="ongoing">Ongoing</option>
              <option value="completed">Completed</option>
              <option value="cancelled">Cancelled</option>
            </select>
          </div>
        </div>

        {/* Competition Grid */}
        {filteredCompetitions.length === 0 ? (
          <Card>
            <CardContent className="p-12 text-center">
              <Trophy className="w-12 h-12 text-gray-300 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">
                No upcoming competitions found
              </h3>
              <p className="text-gray-500">
                {searchTerm || statusFilter !== "upcoming" 
                  ? "Try adjusting your search criteria"
                  : "No upcoming competitions from your preferences are available. Visit Competition Preferences to select competitions."
                }
              </p>
            </CardContent>
          </Card>
        ) : (
          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
            {filteredCompetitions.map((competition) => (
              <Card 
                key={competition.id} 
                className="hover:shadow-lg transition-shadow cursor-pointer"
                data-testid={`competition-card-${competition.id}`}
              >
                <CardHeader>
                  <div className="flex justify-between items-start">
                    <CardTitle className="text-lg font-semibold line-clamp-2">
                      {competition.name}
                    </CardTitle>
                    <Badge className={getStatusColor(competition.status || 'upcoming')}>
                      {competition.status || 'upcoming'}
                    </Badge>
                  </div>
                </CardHeader>
                <CardContent className="space-y-3">
                  <div className="flex items-center text-sm text-gray-600 dark:text-gray-300">
                    <MapPin className="w-4 h-4 mr-2" />
                    {competition.city ? `${competition.city}, ${competition.country}` : competition.country}
                  </div>

                  <div className="flex items-center text-sm text-gray-600 dark:text-gray-300">
                    <Calendar className="w-4 h-4 mr-2" />
                    {competition.startDate}
                    {competition.endDate && competition.endDate !== competition.startDate && 
                      ` - ${competition.endDate}`
                    }
                  </div>

                  <div className="flex items-center justify-between">
                    <div className="flex items-center text-sm text-gray-600 dark:text-gray-300">
                      <Users className="w-4 h-4 mr-2" />
                      {competition.participantCount} participants
                    </div>

                    {competition.gradeLevel && (
                      <Badge variant="outline" className="text-xs">
                        {competition.gradeLevel}
                      </Badge>
                    )}
                  </div>

                  {competition.category && (
                    <div className="text-sm text-gray-600 dark:text-gray-300">
                      Category: {competition.category}
                    </div>
                  )}

                  <Button
                    onClick={() => handleViewDrawSheet(competition)}
                    className="w-full mt-4"
                    data-testid={`button-view-draw-${competition.id}`}
                  >
                    <Weight className="w-4 h-4 mr-2" />
                    View Weight Categories
                  </Button>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Competition Divisions Dialog */}
        <Dialog open={drawSheetOpen} onOpenChange={setDrawSheetOpen}>
          <DialogContent className="max-w-5xl max-h-[90vh] overflow-y-auto" data-testid="divisions-dialog">
            <DialogHeader>
              <DialogTitle className="text-xl flex items-center gap-2">
                <Weight className="h-5 w-5" />
                Senior Division Weight Categories - {selectedCompetition?.name}
              </DialogTitle>
            </DialogHeader>

            {selectedCompetition && (
              <div className="space-y-4">
                {/* Competition Details */}
                <div className="p-3 bg-gray-50 dark:bg-gray-800 rounded-lg space-y-1">
                  <div className="flex items-center gap-2 text-sm">
                    <MapPin className="h-4 w-4" />
                    {selectedCompetition.city}, {selectedCompetition.country}
                  </div>
                  <div className="flex items-center gap-2 text-sm">
                    <Calendar className="h-4 w-4" />
                    {selectedCompetition.startDate}
                  </div>
                  {selectedCompetition.gradeLevel && (
                    <Badge variant="outline" className="text-xs">
                      {selectedCompetition.gradeLevel}
                    </Badge>
                  )}
                </div>

                {/* Controls */}
                <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
                  <div className="flex items-center gap-2">
                    <Filter className="h-4 w-4" />
                    <Input
                      placeholder="Filter by weight category..."
                      value={divisionsSearchTerm}
                      onChange={(e) => setDivisionsSearchTerm(e.target.value)}
                      className="w-full sm:w-64"
                      data-testid="input-search-divisions"
                    />
                  </div>

                  {liveParticipants && (
                    <Button 
                      onClick={handleExportCSV} 
                      variant="outline"
                      data-testid="button-export"
                    >
                      <Download className="h-4 w-4 mr-2" />
                      Export CSV
                    </Button>
                  )}
                </div>

                {/* Loading State */}
                {liveParticipantsLoading && (
                  <div className="flex items-center justify-center py-8">
                    <div className="text-center space-y-2">
                      <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
                      <p className="text-sm text-gray-500">Loading participants from SimplyCompete...</p>
                    </div>
                  </div>
                )}

                {/* Error State */}
                {liveParticipantsError && (
                  <div className="text-center py-8">
                    <p className="text-red-500" data-testid="error-message">
                      Failed to load participants. Please try again.
                    </p>
                  </div>
                )}

                {/* Participants Display */}
                {liveParticipants && !liveParticipantsLoading && (
                  <>
                    {filteredParticipants.length === 0 ? (
                      <div className="text-center py-8">
                        <p className="text-gray-500" data-testid="no-participants">
                          {divisionsSearchTerm 
                            ? "No weight categories match your search." 
                            : "No participants found for this competition."
                          }
                        </p>
                      </div>
                    ) : (
                      <div className="space-y-6">
                        <div className="flex items-center gap-4 text-sm text-gray-600">
                          <Badge variant="secondary" className="px-3">
                            <Users className="h-3 w-3 mr-1" />
                            {filteredParticipants.reduce((sum, cat) => sum + cat.athleteCount, 0)} total athletes
                          </Badge>
                          <Badge variant="secondary" className="px-3">
                            {filteredParticipants.length} weight categories
                          </Badge>
                        </div>

                        <div className="space-y-6" data-testid="participants-list">
                          {filteredParticipants.map((category, categoryIndex) => (
                            <div key={categoryIndex} className="border rounded-lg p-4 bg-gray-50 dark:bg-gray-800">
                              <div className="flex items-center justify-between mb-4">
                                <h3 className="text-lg font-semibold text-gray-900 dark:text-white">
                                  {category.weightCategory}
                                </h3>
                                <Badge variant="outline" className="ml-2">
                                  {category.athleteCount} athletes
                                </Badge>
                              </div>

                              <div className="grid gap-3 sm:grid-cols-2 lg:grid-cols-3">
                                {category.athletes.map((athlete, athleteIndex) => (
                                  <div 
                                    key={athleteIndex} 
                                    className="flex items-center space-x-3 p-3 bg-white dark:bg-gray-700 rounded-lg border"
                                    data-testid={`athlete-${categoryIndex}-${athleteIndex}`}
                                  >
                                    {athlete.avatar && (
                                      <img 
                                        src={athlete.avatar} 
                                        alt={athlete.name}
                                        className="w-10 h-10 rounded-full object-cover"
                                        onError={(e) => {
                                          (e.target as HTMLImageElement).style.display = 'none';
                                        }}
                                      />
                                    )}
                                    <div className="flex-1 min-w-0">
                                      <div className="font-medium text-gray-900 dark:text-white truncate">
                                        {athlete.name}
                                      </div>
                                      <div className="text-sm text-gray-500 dark:text-gray-400">
                                        {athlete.country} ‚Ä¢ {athlete.license}
                                      </div>
                                      {athlete.club && (
                                        <div className="text-xs text-gray-400 dark:text-gray-500 truncate">
                                          {athlete.club}
                                        </div>
                                      )}
                                    </div>
                                  </div>
                                ))}
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </>
                )}
              </div>
            )}
          </DialogContent>
        </Dialog>
      </div>
    </>
  );
}

client/src/pages/competition-divisions.t.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import Header from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import type { Competition } from "@shared/schema";
import { 
  Calendar, 
  MapPin, 
  Users, 
  Trophy,
  Download,
  Weight,
  Filter
} from "lucide-react";

interface DivisionData {
  eventName: string;
  weightCategory: string;
  athleteCount: number;
  athletes: string[];
}

export default function CompetitionDivisions() {
  const [selectedCompetitionId, setSelectedCompetitionId] = useState<string>("");
  const [searchTerm, setSearchTerm] = useState("");

  // Fetch all competitions
  const { data: competitions, isLoading: competitionsLoading } = useQuery<Competition[]>({
    queryKey: ['/api/competitions'],
  });

  // Fetch divisions for selected competition
  const { data: divisions, isLoading: divisionsLoading, error } = useQuery<DivisionData[]>({
    queryKey: [`/api/competitions/${selectedCompetitionId}/divisions`],
    enabled: !!selectedCompetitionId,
  });

  // Filter upcoming competitions
  const upcomingCompetitions = competitions?.filter(comp => comp.status === "upcoming") || [];

  // Filter divisions based on search term
  const filteredDivisions = divisions?.filter(division =>
    division.weightCategory.toLowerCase().includes(searchTerm.toLowerCase())
  ) || [];

  const selectedCompetition = competitions?.find(comp => comp.id.toString() === selectedCompetitionId);

  const handleExportCSV = () => {
    if (!divisions || !selectedCompetition) return;

    const csvContent = [
      ["Weight Category", "Athlete Count"],
      ...filteredDivisions.map(division => [
        division.weightCategory,
        division.athleteCount.toString()
      ])
    ].map(row => row.join(",")).join("\n");

    const blob = new Blob([csvContent], { type: "text/csv" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `${selectedCompetition.name}_divisions.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="space-y-6 p-4 lg:p-6">
      <Header
        title="Competition Divisions"
        description="View athlete counts by weight category in Senior Division"
      />

      {/* Competition Selection */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Trophy className="h-5 w-5" />
            Select Competition
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid gap-4 md:grid-cols-2">
            <div className="space-y-2">
              <Label htmlFor="competition-select">Competition</Label>
              <Select 
                value={selectedCompetitionId} 
                onValueChange={setSelectedCompetitionId}
                data-testid="select-competition"
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select a competition..." />
                </SelectTrigger>
                <SelectContent>
                  {upcomingCompetitions.map((competition) => (
                    <SelectItem key={competition.id} value={competition.id.toString()}>
                      {competition.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {selectedCompetition && (
              <div className="space-y-2">
                <Label>Competition Details</Label>
                <div className="p-3 bg-gray-50 dark:bg-gray-800 rounded-lg space-y-1">
                  <div className="flex items-center gap-2 text-sm">
                    <MapPin className="h-4 w-4" />
                    {selectedCompetition.city}, {selectedCompetition.country}
                  </div>
                  <div className="flex items-center gap-2 text-sm">
                    <Calendar className="h-4 w-4" />
                    {selectedCompetition.startDate}
                  </div>
                  <Badge variant="outline" className="text-xs">
                    {selectedCompetition.gradeLevel}
                  </Badge>
                </div>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Division Results */}
      {selectedCompetitionId && (
        <Card>
          <CardHeader>
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
              <CardTitle className="flex items-center gap-2">
                <Weight className="h-5 w-5" />
                Senior Division - Weight Categories
              </CardTitle>

              <div className="flex flex-col sm:flex-row gap-2">
                <div className="flex items-center gap-2">
                  <Filter className="h-4 w-4" />
                  <Input
                    placeholder="Filter by weight category..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full sm:w-64"
                    data-testid="input-search"
                  />
                </div>

                {divisions && (
                  <Button 
                    onClick={handleExportCSV} 
                    variant="outline"
                    data-testid="button-export"
                  >
                    <Download className="h-4 w-4 mr-2" />
                    Export CSV
                  </Button>
                )}
              </div>
            </div>
          </CardHeader>

          <CardContent>
            {divisionsLoading && (
              <div className="flex items-center justify-center py-8">
                <div className="text-center space-y-2">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
                  <p className="text-sm text-gray-500">Loading divisions...</p>
                </div>
              </div>
            )}

            {error && (
              <div className="text-center py-8">
                <p className="text-red-500" data-testid="error-message">
                  Failed to load divisions. Please try again.
                </p>
              </div>
            )}

            {divisions && !divisionsLoading && (
              <>
                {filteredDivisions.length === 0 ? (
                  <div className="text-center py-8">
                    <p className="text-gray-500" data-testid="no-divisions">
                      {searchTerm 
                        ? "No weight categories match your search." 
                        : "No divisions found for this competition."
                      }
                    </p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    <div className="flex items-center gap-4 text-sm text-gray-600">
                      <Badge variant="secondary" className="px-3">
                        <Users className="h-3 w-3 mr-1" />
                        {filteredDivisions.reduce((sum, div) => sum + div.athleteCount, 0)} total athletes
                      </Badge>
                      <Badge variant="secondary" className="px-3">
                        {filteredDivisions.length} weight categories
                      </Badge>
                    </div>

                    <Table data-testid="table-divisions">
                      <TableHeader>
                        <TableRow>
                          <TableHead>Weight Category</TableHead>
                          <TableHead className="text-right">Athlete Count</TableHead>
                        </TableRow>
                      </TableHeader>
                      <TableBody>
                        {filteredDivisions.map((division, index) => (
                          <TableRow key={index} data-testid={`row-division-${index}`}>
                            <TableCell className="font-medium">
                              {division.weightCategory}
                            </TableCell>
                            <TableCell className="text-right">
                              <Badge variant="outline" data-testid={`count-${index}`}>
                                {division.athleteCount}
                              </Badge>
                            </TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </div>
                )}
              </>
            )}
          </CardContent>
        </Card>
      )}

      {!selectedCompetitionId && (
        <Card>
          <CardContent className="text-center py-12">
            <Trophy className="h-12 w-12 mx-auto text-gray-400 mb-4" />
            <p className="text-gray-500" data-testid="no-competition-selected">
              Select a competition above to view its divisions and athlete counts.
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

scripts/sync-competitions.ts.content:
#!/usr/bin/env tsx

import { db } from '../server/db';
import { competitions } from '../shared/schema';
import { eq, and, sql } from 'drizzle-orm';

interface SimplyCompeteEvent {
  id: string;
  name: string;
  startDate: string;
  endDate?: string;
  location: {
    country?: string;
    city?: string;
  };
  [key: string]: any;
}

interface SyncResult {
  total: number;
  matched: number;
  updated: number;
  skipped: number;
  errors: number;
}

const BASE_URL = 'https://worldtkd.simplycompete.com/events/eventList';
const ITEMS_PER_PAGE = 50;

async function fetchCompetitionsFromAPI(pageNumber: number = 1): Promise<SimplyCompeteEvent[]> {
  const params = new URLSearchParams({
    da: 'true',
    eventType: 'All',
    invitationStatus: 'all',
    isArchived: 'false',
    itemsPerPage: ITEMS_PER_PAGE.toString(),
    pageNumber: pageNumber.toString(),
  });

  const url = `${BASE_URL}?${params.toString()}`;
  console.log(`üì° Fetching page ${pageNumber} from: ${url}`);

  // Get authentication credentials from environment variables if available
  const apiKey = process.env.SIMPLYCOMPETE_API_KEY;
  const authToken = process.env.SIMPLYCOMPETE_AUTH_TOKEN;
  const cookie = process.env.SIMPLYCOMPETE_COOKIE;

  try {
    const headers: Record<string, string> = {
      'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36',
      'Accept': 'application/json, text/plain, */*',
      'Accept-Language': 'en-US,en;q=0.9',
      'Referer': 'https://worldtkd.simplycompete.com/',
      'Origin': 'https://worldtkd.simplycompete.com',
    };

    // Add authentication if available
    if (apiKey) {
      headers['X-API-Key'] = apiKey;
    }
    if (authToken) {
      headers['Authorization'] = `Bearer ${authToken}`;
    }
    if (cookie) {
      headers['Cookie'] = cookie;
    }

    const response = await fetch(url, { headers });

    if (!response.ok) {
      if (response.status === 403) {
        throw new Error(
          'Authentication required. The SimplyCompete API requires authentication credentials.\n' +
          'Please set one of the following environment variables:\n' +
          '  - SIMPLYCOMPETE_API_KEY\n' +
          '  - SIMPLYCOMPETE_AUTH_TOKEN\n' +
          'Or contact SimplyCompete for API access credentials.'
        );
      }
      throw new Error(`HTTP error! status: ${response.status} ${response.statusText}`);
    }

    const data = await response.json();

    // Extract events array from response (adjust based on actual API structure)
    const events = Array.isArray(data) ? data : data.events || data.data || [];

    console.log(`‚úÖ Fetched ${events.length} events from page ${pageNumber}`);
    return events;
  } catch (error) {
    if (pageNumber === 1) {
      console.error(`\n‚ùå Error fetching data from SimplyCompete API:`);
      console.error(error instanceof Error ? error.message : error);
      console.error(`\nüí° Troubleshooting tips:`);
      console.error(`   1. Verify the API endpoint is accessible: ${BASE_URL}`);
      console.error(`   2. Check if authentication credentials are required`);
      console.error(`   3. Contact SimplyCompete support for API access\n`);
    }
    return [];
  }
}

async function fetchAllCompetitions(): Promise<SimplyCompeteEvent[]> {
  const allEvents: SimplyCompeteEvent[] = [];
  let pageNumber = 1;
  let hasMorePages = true;

  while (hasMorePages) {
    const events = await fetchCompetitionsFromAPI(pageNumber);

    // Fail fast if first page returns no events (likely auth/API error)
    if (pageNumber === 1 && events.length === 0) {
      throw new Error(
        'Failed to fetch any events from the first page. This typically indicates:\n' +
        '  - Authentication failure (403 Forbidden)\n' +
        '  - API endpoint unavailable\n' +
        '  - Network connectivity issues\n' +
        'Please check the error messages above and resolve before continuing.'
      );
    }

    if (events.length === 0) {
      hasMorePages = false;
      console.log(`üìÑ No more pages. Total pages fetched: ${pageNumber - 1}`);
    } else {
      allEvents.push(...events);
      pageNumber++;

      // Safety limit to prevent infinite loops
      if (pageNumber > 100) {
        console.log('‚ö†Ô∏è  Reached safety limit of 100 pages');
        hasMorePages = false;
      }
    }
  }

  return allEvents;
}

function normalizeDate(dateStr: string): string {
  // Normalize date to YYYY-MM-DD format
  try {
    const date = new Date(dateStr);
    return date.toISOString().split('T')[0];
  } catch {
    return dateStr;
  }
}

function normalizeName(name: string): string {
  // Normalize competition names for more reliable matching
  return name
    .toLowerCase()
    .trim()
    .replace(/[^\w\s]/g, '') // Remove punctuation
    .replace(/\s+/g, ' '); // Collapse multiple spaces to single space
}

async function syncCompetitions(): Promise<SyncResult> {
  const result: SyncResult = {
    total: 0,
    matched: 0,
    updated: 0,
    skipped: 0,
    errors: 0,
  };

  console.log('üöÄ Starting competition sync from SimplyCompete API...\n');

  // Fetch all competitions from API
  const apiEvents = await fetchAllCompetitions();
  result.total = apiEvents.length;

  console.log(`\nüìä Total events fetched: ${result.total}`);
  console.log('üîç Matching with local database...\n');

  // Get all existing competitions from database
  const existingCompetitions = await db.select().from(competitions);
  console.log(`üíæ Found ${existingCompetitions.length} existing competitions in database\n`);

  // Process each API event
  for (const apiEvent of apiEvents) {
    try {
      const eventName = apiEvent.name?.trim();
      const eventStartDate = normalizeDate(apiEvent.startDate);

      if (!eventName || !eventStartDate) {
        console.log(`‚è≠Ô∏è  Skipping event with missing name or date`);
        result.skipped++;
        continue;
      }

      // Try to match with existing competition by normalized name and exact date
      const normalizedEventName = normalizeName(eventName);
      const matchedCompetition = existingCompetitions.find((comp) => {
        const normalizedCompName = normalizeName(comp.name);

        // Exact match after normalization
        const exactMatch = normalizedCompName === normalizedEventName;

        // Substring match as fallback (more conservative than before)
        const substringMatch = normalizedCompName.includes(normalizedEventName) ||
                               normalizedEventName.includes(normalizedCompName);

        const dateMatch = normalizeDate(comp.startDate) === eventStartDate;

        // Require exact name match OR substring with date match
        return dateMatch && (exactMatch || substringMatch);
      });

      if (matchedCompetition) {
        // Update existing competition with SimplyCompete data
        const sourceUrl = `https://worldtkd.simplycompete.com/events/${apiEvent.id}`;

        await db
          .update(competitions)
          .set({
            simplyCompeteEventId: apiEvent.id,
            sourceUrl: sourceUrl,
            metadata: apiEvent as any,
            lastSyncedAt: new Date(),
          })
          .where(eq(competitions.id, matchedCompetition.id));

        console.log(`‚úÖ Updated: "${matchedCompetition.name}" ‚Üí SimplyCompete ID: ${apiEvent.id}`);
        result.matched++;
        result.updated++;
      } else {
        console.log(`‚è≠Ô∏è  No match found for: "${eventName}" (${eventStartDate})`);
        result.skipped++;
      }
    } catch (error) {
      console.error(`‚ùå Error processing event "${apiEvent.name}":`, error);
      result.errors++;
    }
  }

  return result;
}

async function main() {
  const args = process.argv.slice(2);

  if (args.includes('--help') || args.includes('-h')) {
    console.log(`
SimplyCompete Competition Sync Tool

Usage:
  tsx scripts/sync-competitions.ts          - Sync all competitions from SimplyCompete API
  tsx scripts/sync-competitions.ts --help   - Show this help message

Description:
  Fetches competitions from the SimplyCompete API and matches them with existing
  competitions in the local database. For each match found, updates the local
  competition record with:
    - SimplyCompete event ID
    - Source URL
    - Full event metadata (JSON)
    - Last sync timestamp

  Matching is done by comparing competition names and start dates.

Authentication:
  The SimplyCompete API requires authentication. Set one of these environment variables:
    SIMPLYCOMPETE_API_KEY      - Your SimplyCompete API key
    SIMPLYCOMPETE_AUTH_TOKEN   - Your authentication token
    SIMPLYCOMPETE_COOKIE       - Session cookie if API requires it

  Example:
    SIMPLYCOMPETE_API_KEY=your_key_here tsx scripts/sync-competitions.ts

  To get API credentials, contact SimplyCompete support or check their developer documentation.

API Endpoint:
  ${BASE_URL}
    `);
    process.exit(0);
  }

  try {
    const result = await syncCompetitions();

    console.log('\n' + '='.repeat(60));
    console.log('üìà Sync Results:');
    console.log('='.repeat(60));
    console.log(`üìä Total API Events:      ${result.total}`);
    console.log(`‚úÖ Matched & Updated:     ${result.updated}`);
    console.log(`‚è≠Ô∏è  Skipped (no match):    ${result.skipped}`);
    console.log(`‚ùå Errors:                ${result.errors}`);
    console.log('='.repeat(60) + '\n');

    if (result.errors > 0) {
      process.exit(1);
    }
  } catch (error) {
    console.error('üí• Fatal error during sync:', error);
    process.exit(1);
  }
}

main();

